          )}
        </div>
      </div>

      {/* Floating Calendar Navigation Panel - Left Side */}
      {viewMode === 'calendar' && isInCalendarView && (
        <>
          <button
            onClick={() => setShowCalendarNav(!showCalendarNav)}
            className={`fixed left-6 top-1/3 bg-white rounded-lg shadow-lg p-2 z-30 hover:bg-gray-50 transition-all duration-500 ${
              isInCalendarView ? 'opacity-100 translate-x-0' : 'opacity-0 -translate-x-10'
            }`}
            style={{ 
              borderColor: '#cec4c1', 
              borderWidth: '1px',
              transform: showCalendarNav && isInCalendarView ? 'translateX(190px)' : 'translateX(0)',
              pointerEvents: isInCalendarView ? 'auto' : 'none'
            }}
            title={showCalendarNav ? 'Hide Calendar Navigation' : 'Show Calendar Navigation'}
          >
            {showCalendarNav ? <PanelLeftClose className="w-4 h-4" style={{ color: '#b48f8f' }} /> : <PanelLeftOpen className="w-4 h-4" style={{ color: '#b48f8f' }} />}
          </button>

          <div 
            className={`fixed left-6 top-1/3 bg-white rounded-lg shadow-xl p-4 z-30 transition-all duration-500 ${
              showCalendarNav && isInCalendarView ? 'opacity-100 translate-x-0' : 
              !showCalendarNav && isInCalendarView ? 'opacity-0 -translate-x-full' :
              'opacity-0 -translate-x-10'
            }`} 
            style={{ 
              borderColor: '#cec4c1', 
              borderWidth: '1px', 
              width: '180px',
              pointerEvents: showCalendarNav && isInCalendarView ? 'auto' : 'none'
            }}
          >
            <h4 className="font-semibold text-sm mb-3" style={{ color: '#4a4a4a' }}>Calendar View</h4>

            <div className="space-y-2 mb-3">
              <button
                onClick={() => handleCalendarViewChange('firstHalf')}
                className={`w-full py-2 text-sm rounded transition-all ${
                  calendarView === 'firstHalf' ? 'text-white' : 'bg-gray-50 hover:bg-gray-100'
                }`}
                style={{ 
                  backgroundColor: calendarView === 'firstHalf' ? '#b48f8f' : '',
                  color: calendarView === 'firstHalf' ? 'white' : '#4a4a4a'
                }}
              >
                July 1-15
              </button>
              <button
                onClick={() => handleCalendarViewChange('secondHalf')}
                className={`w-full py-2 text-sm rounded transition-all ${
                  calendarView === 'secondHalf' ? 'text-white' : 'bg-gray-50 hover:bg-gray-100'
                }`}
                style={{ 
                  backgroundColor: calendarView === 'secondHalf' ? '#b48f8f' : '',
                  color: calendarView === 'secondHalf' ? 'white' : '#4a4a4a'
                }}
              >
                July 16-31
              </button>
              <button
                onClick={() => handleCalendarViewChange('full')}
                className={`w-full py-1 text-xs rounded transition-all ${
                  calendarView === 'full' ? 'text-white' : 'bg-gray-50 hover:bg-gray-100'
                }`}
                style={{ 
                  backgroundColor: calendarView === 'full' ? '#8f93a0' : '',
                  color: calendarView === 'full' ? 'white' : '#8f93a0'
                }}
              >
                Show Full Month
              </button>
            </div>

            <div className="border-t pt-3" style={{ borderColor: '#cec4c1' }}>
              <p className="text-xs mb-2" style={{ color: '#8f93a0' }}>Week View:</p>
              <div className="grid grid-cols-2 gap-1">
                <button
                  onClick={() => handleCalendarViewChange('week1')}
                  className={`py-1 text-xs rounded transition-all ${
                    calendarView === 'week1' ? 'text-white' : 'bg-gray-50 hover:bg-gray-100'
                  }`}
                  style={{ 
                    backgroundColor: calendarView === 'week1' ? '#8f93a0' : '',
                    color: calendarView === 'week1' ? 'white' : '#4a4a4a'
                  }}
                >
                  Week 1
                </button>
                <button
                  onClick={() => handleCalendarViewChange('week2')}
                  className={`py-1 text-xs rounded transition-all ${
                    calendarView === 'week2' ? 'text-white' : 'bg-gray-50 hover:bg-gray-100'
                  }`}
                  style={{ 
                    backgroundColor: calendarView === 'week2' ? '#8f93a0' : '',
                    color: calendarView === 'week2' ? 'white' : '#4a4a4a'
                  }}
                >
                  Week 2
                </button>
                <button
                  onClick={() => handleCalendarViewChange('week3')}
                  className={`py-1 text-xs rounded transition-all ${
                    calendarView === 'week3' ? 'text-white' : 'bg-gray-50 hover:bg-gray-100'
                  }`}
                  style={{ 
                    backgroundColor: calendarView === 'week3' ? '#8f93a0' : '',
                    color: calendarView === 'week3' ? 'white' : '#4a4a4a'
                  }}
                >
                  Week 3
                </button>
                <button
                  onClick={() => handleCalendarViewChange('week4')}
                  className={`py-1 text-xs rounded transition-all ${
                    calendarView === 'week4' ? 'text-white' : 'bg-gray-50 hover:bg-gray-100'
                  }`}
                  style={{ 
                    backgroundColor: calendarView === 'week4' ? '#8f93a0' : '',
                    color: calendarView === 'week4' ? 'white' : '#4a4a4a'
                  }}
                >
                  Week 4+
                </button>
              </div>
            </div>
          </div>
        </>
      )}

      {/* Back to Top Button */}
      {showBackToTop && (
        <button
          onClick={scrollToTop}
          className="fixed bottom-6 right-6 w-12 h-12 rounded-full shadow-lg hover:scale-105 transition-transform duration-200 z-40"
          style={{ backgroundColor: theme.colors.primary }}
        >
          <ChevronUp className="w-6 h-6 text-white mx-auto" />
        </button>
      )}
    </div>
  );
};

export default EventsDashboard;
