import React, { useState } from 'react';
import MainHub from './components/MainHub';
import EventsDashboard from './components/EventsDashboard';
import LinkFactorySupabase from './components/LinkFactorySupabase';
import AdminCenter from './components/AdminCenter';
// Import other tools as they're added
import './App.css';

function App() {
  const [currentTool, setCurrentTool] = useState('hub');

  const handleNavigate = (path) => {
    switch(path) {
      case '/events':
        setCurrentTool('events');
        break;
      case '/link-copier':
        setCurrentTool('link-copier');
        break;
      case '/admin':
        setCurrentTool('admin');
        break;
      case '/analytics':
        setCurrentTool('analytics');
        break;
      case '/users':
        setCurrentTool('users');
        break;
      default:
        setCurrentTool('hub');
    }
  };

  const handleBackToHub = () => {
    setCurrentTool('hub');
  };

  const renderCurrentTool = () => {
    switch(currentTool) {
      case 'events':
        return <EventsDashboard onBackToHub={handleBackToHub} />;
      case 'link-copier':
        return (
          <LinkFactorySupabase 
            onBackToHub={handleBackToHub} 
            onOpenAdmin={() => setCurrentTool('admin')}
          />
        );
      case 'admin':
        return <AdminCenter onBackToHub={handleBackToHub} />;
      case 'analytics':
        return (
          <div className="p-8 text-center">
            <h2>Analytics Dashboard</h2>
            <p>Coming soon...</p>
            <button 
              onClick={handleBackToHub}
              className="mt-4 px-4 py-2 bg-blue-500 text-white rounded"
            >
              Back to Hub
            </button>
          </div>
        );
      case 'users':
        return (
          <div className="p-8 text-center">
            <h2>User Management</h2>
            <p>Coming soon...</p>
            <button 
              onClick={handleBackToHub}
              className="mt-4 px-4 py-2 bg-blue-500 text-white rounded"
            >
              Back to Hub
            </button>
          </div>
        );
      default:
        return <MainHub onNavigate={handleNavigate} />;
    }
  };

  return (
    <div className="App">
      {renderCurrentTool()}
    </div>
  );
}

export default App;
