import React, { useState, useRef } from 'react';
import { Copy, Search, RefreshCw, MapPin, Phone, User, ArrowUp, ExternalLink, Check, Edit3, Save, X, ChevronLeft } from 'lucide-react';

// Theme matching your hub
const theme = {
  colors: {
    primary: '#b48f8f',        // Blush pink - your brand!
    secondary: '#cec4c1',      // Neutral gray-beige
    accent: '#8f93a0',         // Gray-blue highlights
    textPrimary: '#4a4a4a',    // Dark gray
    success: '#22c55e',        // Green
    warning: '#f59e0b',        // Orange
    error: '#ef4444',          // Red
    background: '#f7f5f6',     // Light background
  }
};

// Real gym data from your ClickUp
const realGyms = [
  {
    id: 'ccp',
    name: 'Capital Gymnastics Cedar Park',
    address: '504 Denali Pass, Cedar Park, TX 78613',
    phone: '512-259-9995',
    email: 'info@capgymcpk.com',
    website: 'capgymcpk.com',
    mapLink: 'https://maps.app.goo.gl/FC6WPZbbtHDmLXGfA',
    state: 'TX'
  },
  {
    id: 'cpf',
    name: 'Capital Gymnastics Pflugerville',
    address: '419 Kingston Lacy Blvd, TX 78660',
    phone: '512-251-2439',
    email: 'info@capgympfl.com',
    website: 'capgympfl.com',
    mapLink: 'https://maps.app.goo.gl/A4o7LNBy22csjN4J6',
    state: 'TX'
  },
  {
    id: 'crr',
    name: 'Capital Gymnastics Round Rock',
    address: '4600 Campus Village Dr, Round Rock, TX 78665',
    phone: '512-244-4999',
    email: 'info@capgymrnd.com',
    website: 'capgymrnd.com',
    mapLink: 'https://maps.app.goo.gl/MtWprEfn1WzZCzYS7',
    state: 'TX'
  },
