export default function LinkFactory({ onBackToHub }) {
  const [selectedGyms, setSelectedGyms] = useState([]);
  const [selectedLinkTypes, setSelectedLinkTypes] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedGymLinks, setSelectedGymLinks] = useState({});
  const [copySuccess, setCopySuccess] = useState('');
  const [editingLink, setEditingLink] = useState(null);
  const [editValue, setEditValue] = useState('');
  const [gymLinks, setGymLinks] = useState(realGymLinks);
  const topRef = useRef(null);

  // Get link for specific gym and link type
  const getGymLink = (gymId, linkTypeId) => {
    const link = gymLinks.find(l => l.gymId === gymId && l.linkTypeId === linkTypeId);
    return link?.url || null;
  };

  // Start editing a link
  const startEditingLink = (gymId, linkTypeId) => {
    const currentUrl = getGymLink(gymId, linkTypeId);
    setEditingLink({ gymId, linkTypeId });
    setEditValue(currentUrl || '');
  };

  // Save edited link
  const saveEditedLink = () => {
    if (!editingLink) return;
    
    const { gymId, linkTypeId } = editingLink;
    
    // Update or add the link
    setGymLinks(prev => {
      const existingLinkIndex = prev.findIndex(
        l => l.gymId === gymId && l.linkTypeId === linkTypeId
      );
      
      if (existingLinkIndex >= 0) {
        const newLinks = [...prev];
        newLinks[existingLinkIndex] = { gymId, linkTypeId, url: editValue };
        return newLinks;
      } else {
        return [...prev, { gymId, linkTypeId, url: editValue }];
      }
    });
    
    setEditingLink(null);
    setEditValue('');
    setCopySuccess('‚ú® Link updated successfully!');
    setTimeout(() => setCopySuccess(''), 2000);
  };

  // Cancel editing
  const cancelEditing = () => {
    setEditingLink(null);
    setEditValue('');
  };

  // Copy functionality
  const copyLink = async (url, label) => {
    try {
      await navigator.clipboard.writeText(url);
      setCopySuccess(`‚ú® ${label} copied!`);
      setTimeout(() => setCopySuccess(''), 2000);
    } catch (err) {
      console.error('Failed to copy: ', err);
    }
  };

  const copyContactInfo = async (info, type) => {
    try {
      await navigator.clipboard.writeText(info);
      setCopySuccess(`‚ú® ${type} copied!`);
      setTimeout(() => setCopySuccess(''), 2000);
    } catch (err) {
      console.error('Failed to copy: ', err);
    }
  };

  // Toggle functions
  const toggleGym = (gymId) => {
    setSelectedGyms(prev => 
      prev.includes(gymId) 
        ? prev.filter(id => id !== gymId)
        : [...prev, gymId]
    );
  };

  const toggleLinkType = (linkTypeId) => {
    setSelectedLinkTypes(prev => 
      prev.includes(linkTypeId) 
        ? prev.filter(id => id !== linkTypeId)
        : [...prev, linkTypeId]
    );
  };

  const toggleGymLink = (gymId, linkTypeId) => {
    setSelectedGymLinks(prev => {
      const gymLinks = prev[gymId] || [];
      const newGymLinks = gymLinks.includes(linkTypeId)
        ? gymLinks.filter(id => id !== linkTypeId)
        : [...gymLinks, linkTypeId];
      
      return {
        ...prev,
        [gymId]: newGymLinks
      };
    });
  };

  // Scroll functions
  const scrollToTop = () => {
    topRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  const scrollToGym = (gymId) => {
    const element = document.getElementById(`gym-${gymId}`);
    element?.scrollIntoView({ behavior: 'smooth' });
  };

  // Bulk copy functionality
  const bulkCopyLinks = async () => {
    if (selectedGyms.length === 0 || selectedLinkTypes.length === 0) {
      alert('Please select at least one gym and one link type! üéØ');
      return;
    }

    let copyText = '';
    
    selectedGyms.forEach(gymId => {
      const gym = realGyms.find(g => g.id === gymId);
      if (!gym) return;
      
      copyText += `\nüèÉ‚Äç‚ôÄÔ∏è ${gym.name}\n`;
      
      selectedLinkTypes.forEach(linkTypeId => {
        const linkType = linkTypes.find(lt => lt.id === linkTypeId);
        const url = getGymLink(gymId, linkTypeId);
        
        if (url && linkType) {
          copyText += `${linkType.emoji} ${linkType.label}: ${url}\n`;
        }
      });
      
      copyText += '\n';
    });
    
    try {
      await navigator.clipboard.writeText(copyText.trim());
      setCopySuccess(`‚ú® Bulk copied ${selectedGyms.length} gyms √ó ${selectedLinkTypes.length} link types!`);
      setTimeout(() => setCopySuccess(''), 3000);
    } catch (err) {
      console.error('Failed to copy: ', err);
      alert('Copy failed! Please try again.');
    }
  };

  // Clear all filters
  const clearAllFilters = () => {
    setSelectedGyms([]);
    setSelectedLinkTypes([]);
    setSelectedGymLinks({});
    setSearchTerm('');
  };

  const filteredGyms = selectedGyms.length > 0 
    ? realGyms.filter(gym => selectedGyms.includes(gym.id))
    : realGyms.filter(gym => 
        searchTerm === '' || 
        gym.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
        gym.state.toLowerCase().includes(searchTerm.toLowerCase())
      );

  const totalSelectedLinks = Object.values(selectedGymLinks).reduce((sum, links) => sum + links.length, 0);

  return (
    <div className="min-h-screen" style={{ backgroundColor: theme.colors.background }}>
      <div ref={topRef}></div>
      
      {/* Copy Success Notification */}
      {copySuccess && (
        <div className="fixed top-4 right-4 z-50 bg-green-500 text-white px-4 py-2 rounded-lg shadow-lg flex items-center gap-2">
          <Check className="w-4 h-4" />
          {copySuccess}
        </div>
      )}

      {/* Sidebar */}
      <div className="fixed left-0 top-0 w-64 h-screen bg-white border-r overflow-y-auto" style={{ borderColor: '#d5c2c2' }}>
        <div className="p-4">
          {/* Back to Hub Button */}
          {onBackToHub && (
            <button
              onClick={onBackToHub}
              className="flex items-center gap-2 w-full p-3 mb-4 rounded-lg transition-all duration-200 hover:scale-105"
              style={{ 
                backgroundColor: theme.colors.primary,
                color: 'white'
              }}
            >
              <ChevronLeft className="w-4 h-4" />
              ‚ú® Back to Hub
            </button>
          )}

          <h3 className="text-sm font-semibold text-gray-900 mb-4">üè≠ Jayme's Link Factory</h3>
          
          <div className="mb-6">
            <div className="flex items-center gap-2 text-sm text-gray-600 mb-3">
              <span>‚úàÔ∏è</span>
              <span>Jump to Gym</span>
            </div>
            
            <div className="space-y-2">
              {realGyms.map(gym => (
                <button
                  key={gym.id}
                  onClick={() => scrollToGym(gym.id)}
                  className="w-full p-3 rounded-lg text-white font-medium transition-all hover:shadow-md hover:scale-[1.02] text-left"
                  style={{ 
                    background: `linear-gradient(135deg, ${theme.colors.primary} 0%, #d4a5a5 100%)`,
                    border: '1px solid #c89999'
                  }}
                  onMouseEnter={(e) => {
                    e.currentTarget.style.background = 'linear-gradient(135deg, #d4a5a5 0%, #e6b5b5 100%)';
                  }}
                  onMouseLeave={(e) => {
                    e.currentTarget.style.background = `linear-gradient(135deg, ${theme.colors.primary} 0%, #d4a5a5 100%)`;
                  }}
                >
                  <div className="flex items-center gap-2">
                    <span className="text-lg">üè¢</span>
                    <div>
                      <div className="font-semibold text-sm">{gym.name}</div>
                      <div className="text-xs opacity-90">{gym.id.toUpperCase()} ‚Ä¢ {gym.state}</div>
                    </div>
                  </div>
                </button>
              ))}
            </div>
          </div>
          
          <div className="border-t pt-4 space-y-2" style={{ borderColor: '#d5c2c2' }}>
            <button 
              onClick={clearAllFilters}
              className="flex items-center gap-2 text-sm text-gray-600 hover:text-gray-900 w-full text-left p-2 rounded hover:bg-gray-50"
            >
              <span>üîΩ</span>
              Clear Filters
            </button>
          </div>
        </div>
      </div>

      {/* Main Content */}
      <div className="ml-64 p-4">
        {/* Header */}
        <div className="rounded-lg p-6 mb-6 text-white relative overflow-hidden" style={{ background: `linear-gradient(to right, ${theme.colors.primary}, #A38F85)` }}>
          {/* Sparkle decorations */}
          <div className="absolute top-2 right-2 text-2xl animate-pulse">‚ú®</div>
          <div className="absolute bottom-2 left-2 text-xl animate-pulse">üí´</div>
          <div className="absolute top-1/2 right-1/4 text-lg animate-pulse">‚≠ê</div>
          
          <div className="flex items-center justify-between relative z-10">
            <div>
              <div className="flex items-center gap-3 mb-2">
                <span className="text-2xl">üè≠</span>
                <h1 className="text-2xl font-bold">Jayme's Link Factory</h1>
                <button className="bg-yellow-500 text-yellow-900 px-3 py-1 rounded text-sm font-medium">
                  ‚ú® Professional Tools
                </button>
              </div>
              <p style={{ color: '#f0e6e6' }}>Your sophisticated gymnastics link management system</p>
            </div>
            <div style={{ color: '#f0e6e6' }}>
              üìÖ {new Date().toLocaleDateString()}
            </div>
          </div>
        </div>

        {/* Stats Cards */}
        <div className="grid grid-cols-3 gap-6 mb-6">
          <div className="bg-white rounded-lg shadow p-6" style={{ borderColor: '#d5c2c2', border: '1px solid' }}>
            <div className="flex items-center gap-3">
              <div className="w-10 h-10 rounded-lg flex items-center justify-center" style={{ backgroundColor: '#CDC6C3' }}>
                <MapPin className="w-5 h-5" style={{ color: theme.colors.primary }} />
              </div>
              <div>
                <div className="text-sm text-gray-500">Total Gyms</div>
                <div className="text-2xl font-bold text-gray-900">{realGyms.length}</div>
              </div>
            </div>
          </div>
          
          <div className="bg-white rounded-lg shadow p-6" style={{ borderColor: '#d5c2c2', border: '1px solid' }}>
            <div className="flex items-center gap-3">
              <div className="w-10 h-10 rounded-lg flex items-center justify-center" style={{ backgroundColor: '#CDC6C3' }}>
                <User className="w-5 h-5" style={{ color: theme.colors.primary }} />
              </div>
              <div>
                <div className="text-sm text-gray-500">Selected Gyms</div>
                <div className="text-2xl font-bold text-gray-900">{selectedGyms.length}</div>
              </div>
            </div>
          </div>
          
          <div className="bg-white rounded-lg shadow p-6" style={{ borderColor: '#d5c2c2', border: '1px solid' }}>
            <div className="flex items-center gap-3">
              <div className="w-10 h-10 rounded-lg flex items-center justify-center" style={{ backgroundColor: '#CDC6C3' }}>
                <Copy className="w-5 h-5" style={{ color: theme.colors.primary }} />
              </div>
              <div>
                <div className="text-sm text-gray-500">Selected Links</div>
                <div className="text-2xl font-bold text-gray-900">{totalSelectedLinks}</div>
              </div>
            </div>
          </div>
        </div>

        {/* BULK COPY BUTTON - The main feature! */}
        <div className="mb-6">
          <button
            onClick={bulkCopyLinks}
            disabled={selectedGyms.length === 0 || selectedLinkTypes.length === 0}
            className={`w-full p-4 rounded-lg font-bold text-lg transition-all duration-200 flex items-center justify-center gap-3 ${
              selectedGyms.length > 0 && selectedLinkTypes.length > 0
                ? 'text-white shadow-lg hover:shadow-xl hover:scale-[1.02] transform'
                : 'text-gray-400 bg-gray-100 cursor-not-allowed'
            }`}
            style={{ 
              background: selectedGyms.length > 0 && selectedLinkTypes.length > 0
                ? `linear-gradient(135deg, ${theme.colors.primary} 0%, #d4a5a5 100%)`
                : '#f3f4f6',
              border: '2px solid',
              borderColor: selectedGyms.length > 0 && selectedLinkTypes.length > 0
                ? theme.colors.primary
                : '#d1d5db'
            }}
          >
            <Copy className="w-6 h-6" />
            {selectedGyms.length > 0 && selectedLinkTypes.length > 0 
              ? `‚ú® COPY ALL LINKS (${selectedGyms.length} gyms √ó ${selectedLinkTypes.length} link types)`
              : 'üéØ Select gyms & link types to bulk copy'
            }
          </button>
        </div>

        {/* Selection Panels */}
        <div className="grid grid-cols-5 gap-4 mb-4">
          {/* Select Gyms */}
          <div className="col-span-2 bg-white rounded-lg shadow" style={{ borderColor: '#d5c2c2', border: '1px solid' }}>
            <div className="p-2 rounded-t-lg" style={{ backgroundColor: '#CDC6C3' }}>
              <div className="flex items-center gap-2">
                <span className="text-lg">üè¢</span>
                <h3 className="font-semibold text-gray-900">Select Gyms</h3>
              </div>
            </div>
            
            <div className="p-2">
              <div className="flex gap-2 mb-2">
                <button 
                  onClick={() => setSelectedGyms(realGyms.map(g => g.id))}
                  className="text-white px-3 py-1 rounded text-sm transition-colors"
                  style={{ backgroundColor: theme.colors.primary }}
                >
                  Select All Gyms
                </button>
                <button 
                  onClick={() => setSelectedGyms([])}
                  className="bg-gray-200 text-gray-700 px-3 py-1 rounded text-sm hover:bg-gray-300 transition-colors"
                >
                  Clear Gyms
                </button>
              </div>
              
              <div className="space-y-1">
                {realGyms.map(gym => (
                  <label key={gym.id} className="flex items-center gap-3 p-2 hover:bg-gray-50 rounded cursor-pointer">
                    <input
                      type="checkbox"
                      checked={selectedGyms.includes(gym.id)}
                      onChange={() => toggleGym(gym.id)}
                      className="rounded w-5 h-5"
                      style={{ accentColor: theme.colors.primary }}
                    />
                    <div>
                      <div className="font-medium text-gray-900 text-base">{gym.name}</div>
                      <div className="text-sm text-gray-500">({gym.id.toUpperCase()}) ‚Ä¢ {gym.state}</div>
                    </div>
                  </label>
                ))}
              </div>
            </div>
          </div>

          {/* Select Link Types */}
          <div className="col-span-3 bg-white rounded-lg shadow" style={{ borderColor: '#d5c2c2', border: '1px solid' }}>
            <div className="p-2 rounded-t-lg" style={{ backgroundColor: '#CDC6C3' }}>
              <div className="flex items-center justify-between">
                <div className="flex items-center gap-2">
                  <span className="text-lg">üîó</span>
                  <h3 className="font-semibold text-gray-900">Select Link Types</h3>
                </div>
                <button className="text-gray-600 hover:text-gray-800 text-sm">
                  ‚öôÔ∏è Manage
                </button>
              </div>
            </div>
            
            <div className="p-2">
              <div className="flex gap-2 mb-2">
                <button 
                  onClick={() => setSelectedLinkTypes(linkTypes.map(lt => lt.id))}
                  className="text-white px-3 py-1 rounded text-sm transition-colors"
                  style={{ backgroundColor: '#A38F85' }}
                >
                  Select All
                </button>
                <button 
                  onClick={() => setSelectedLinkTypes([])}
                  className="bg-gray-200 text-gray-700 px-3 py-1 rounded text-sm hover:bg-gray-300 transition-colors"
                >
                  Clear All
                </button>
              </div>
              
              <div className="grid grid-cols-2 gap-1">
                {linkTypes.map(linkType => (
                  <label key={linkType.id} className="flex items-center gap-2 text-base cursor-pointer hover:bg-gray-50 p-2 rounded">
                    <input
                      type="checkbox"
                      checked={selectedLinkTypes.includes(linkType.id)}
                      onChange={() => toggleLinkType(linkType.id)}
                      className="rounded w-5 h-5"
                      style={{ accentColor: theme.colors.primary }}
                    />
                    <span className="text-lg">{linkType.emoji}</span>
                    <span className="text-gray-700">{linkType.label}</span>
                  </label>
                ))}
              </div>
            </div>
          </div>
        </div>

        {/* Search Bar */}
        <div className="bg-white rounded-lg shadow p-2 mb-4" style={{ borderColor: '#d5c2c2', border: '1px solid' }}>
          <div className="flex items-center gap-3">
            <div className="relative flex-1">
              <Search className="w-4 h-4 text-gray-400 absolute left-3 top-1/2 transform -translate-y-1/2" />
              <input
                type="text"
                placeholder="Search gyms by name or location..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="w-full rounded px-10 py-2 text-sm"
                style={{ borderColor: '#d5c2c2', border: '1px solid' }}
              />
            </div>
            <button 
              className="flex items-center gap-1 text-white px-3 py-2 rounded text-xs transition-colors"
              style={{ backgroundColor: theme.colors.primary }}
            >
              <RefreshCw className="w-3 h-3" />
              Refresh
            </button>
          </div>
        </div>

        {/* Results */}
        <div className="mb-4">
          <div className="flex items-center justify-between mb-3">
            <h2 className="text-lg font-semibold text-gray-900">üè≠ Factory Results</h2>
            <div className="px-3 py-1 rounded-full text-xs font-medium text-white" style={{ backgroundColor: theme.colors.primary }}>
              ‚ú® {filteredGyms.length} results
            </div>
          </div>

          <div className="space-y-4">
            {filteredGyms.map(gym => (
              <div key={gym.id} id={`gym-${gym.id}`} className="bg-white rounded-lg shadow overflow-hidden" style={{ borderColor: '#d5c2c2', border: '1px solid' }}>
                {/* Gym Header */}
                <div className="text-white p-3 relative" style={{ background: `linear-gradient(to right, ${theme.colors.primary}, #A38F85)` }}>
                  <div className="absolute top-2 right-16 text-lg animate-pulse">‚ú®</div>
                  <div className="flex items-center justify-between relative z-10">
                    <h3 className="text-lg font-bold">{gym.name}</h3>
                    <div className="flex items-center gap-2">
                      <button
                        onClick={scrollToTop}
                        className="bg-white bg-opacity-20 hover:bg-opacity-30 p-2 rounded-full transition-all"
                        title="Back to top"
                      >
                        <ArrowUp className="w-4 h-4" />
                      </button>
                      <div className="text-sm font-medium" style={{ color: '#f0e6e6' }}>{gym.id.toUpperCase()}</div>
                    </div>
                  </div>
                  <div className="flex items-center gap-4 mt-1 text-xs" style={{ color: '#f0e6e6' }}>
                    <div className="flex items-center gap-1">
                      <MapPin className="w-3 h-3" />
                      <span>{gym.address}</span>
                      <Copy 
                        className="w-3 h-3 cursor-pointer hover:text-white transition-colors" 
                        onClick={() => copyContactInfo(gym.address, 'Address')}
                      />
                    </div>
                    <div className="flex items-center gap-1">
                      <Phone className="w-3 h-3" />
                      <span>{gym.phone}</span>
                      <Copy 
                        className="w-3 h-3 cursor-pointer hover:text-white transition-colors"
                        onClick={() => copyContactInfo(gym.phone, 'Phone')}
                      />
                    </div>
                  </div>
                </div>

                {/* Links Grid - 5 Columns */}
                <div className="p-4">
                  <div className="grid grid-cols-5 gap-3">
                    {linkTypes.map(linkType => {
                      const url = getGymLink(gym.id, linkType.id);
                      const isSelected = selectedGymLinks[gym.id]?.includes(linkType.id) || false;
                      const isEditing = editingLink?.gymId === gym.id && editingLink?.linkTypeId === linkType.id;
                      
                      return (
                        <div key={linkType.id} className={`p-2 rounded transition-colors ${
                          url 
                            ? 'hover:bg-gray-50' 
                            : 'bg-gray-50 hover:bg-gray-100'
                        } ${isSelected ? 'bg-blue-50' : ''}`}
                        style={{ 
                          borderColor: isSelected ? theme.colors.primary : '#d5c2c2', 
                          border: '1px solid',
                          minHeight: '80px'
                        }}>
                          
                          {/* Edit Mode */}
                          {isEditing ? (
                            <div className="space-y-1">
                              <div className="flex items-center gap-1">
                                <span className="text-base">{linkType.emoji}</span>
                                <span className="text-xs font-medium text-gray-900">{linkType.label}</span>
                              </div>
                              <input
                                type="text"
                                value={editValue}
                                onChange={(e) => setEditValue(e.target.value)}
                                placeholder="Enter URL or info..."
                                className="w-full text-xs p-1 border rounded"
                                style={{ borderColor: '#d5c2c2' }}
                                autoFocus
                              />
                              <div className="flex items-center gap-1">
                                <button
                                  onClick={saveEditedLink}
                                  className="flex items-center gap-1 px-2 py-1 bg-green-500 text-white rounded text-xs hover:bg-green-600"
                                >
                                  <Save className="w-3 h-3" />
                                  Save
                                </button>
                                <button
                                  onClick={cancelEditing}
                                  className="flex items-center gap-1 px-2 py-1 bg-gray-500 text-white rounded text-xs hover:bg-gray-600"
                                >
                                  <X className="w-3 h-3" />
                                  Cancel
                                </button>
                              </div>
                            </div>
                          ) : (
                            /* Normal Mode */
                            <div className="h-full flex flex-col">
                              <div className="flex items-start gap-2 mb-1">
                                <input
                                  type="checkbox"
                                  checked={isSelected}
                                  onChange={() => toggleGymLink(gym.id, linkType.id)}
                                  className="rounded w-4 h-4 mt-0.5"
                                  style={{ accentColor: theme.colors.primary }}
                                />
                                <span className="text-base">{linkType.emoji}</span>
                                <div className="flex-1 min-w-0">
                                  <div className={`text-xs font-medium truncate ${url ? 'text-gray-900' : 'text-gray-500'}`}>
                                    {linkType.label}
                                  </div>
                                  <div className={`text-xs ${url ? 'text-green-600' : 'text-orange-500'}`}>
                                    {url ? '‚úì Available' : 'No data'}
                                  </div>
                                </div>
                              </div>
                              
                              {/* Action Buttons */}
                              <div className="flex items-center justify-end gap-1 mt-auto">
                                {url && (
                                  <>
                                    {(linkType.id === 'pricing' || linkType.id === 'pricing_4_week' || linkType.id === 'pricing_5_week') ? (
                                      <div 
                                        className="w-3 h-3 cursor-pointer transition-colors text-xs"
                                        style={{ color: theme.colors.primary }}
                                        onClick={() => {
                                          const formattedText = url.replace(/\\n/g, '\n');
                                          alert(formattedText);
                                        }}
                                        title="View pricing"
                                      >
                                        üëÅÔ∏è
                                      </div>
                                    ) : (
                                      <ExternalLink 
                                        className="w-3 h-3 cursor-pointer transition-colors"
                                        style={{ color: theme.colors.primary }}
                                        onClick={() => window.open(url, '_blank')}
                                        title="Open link"
                                      />
                                    )}
                                    <Copy 
                                      className="w-3 h-3 text-gray-400 cursor-pointer hover:text-gray-600 transition-colors"
                                      onClick={() => {
                                        const copyText = (linkType.id === 'pricing' || linkType.id === 'pricing_4_week' || linkType.id === 'pricing_5_week')
                                          ? url.replace(/\\n/g, '\n')
                                          : url;
                                        copyLink(copyText, linkType.label);
                                      }}
                                      title="Copy"
                                    />
                                  </>
                                )}
                                <Edit3 
                                  className="w-3 h-3 text-gray-400 cursor-pointer hover:text-gray-600 transition-colors"
                                  onClick={() => startEditingLink(gym.id, linkType.id)}
                                  title={url ? "Edit link" : "Add link"}
                                />
                              </div>
                            </div>
                          )}
                        </div>
                      );
                    })}
                  </div>
                  
                  {/* Gym Action Buttons */}
                  <div className="flex items-center justify-between mt-4 pt-3" style={{ borderTop: '1px solid #d5c2c2' }}>
                    <button 
                      className="flex items-center gap-1 text-white px-3 py-2 rounded text-xs transition-colors"
                      style={{ backgroundColor: '#A38F85' }}
                    >
                      <Copy className="w-3 h-3" />
                      Copy Selected ({selectedGymLinks[gym.id]?.length || 0})
                    </button>
                    <button 
                      onClick={scrollToTop}
                      className="flex items-center gap-1 text-gray-600 hover:text-gray-800 text-xs transition-colors"
                    >
                      <ArrowUp className="w-3 h-3" />
                      ‚ú® Back to Top
                    </button>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
}
