import React, { useState, useMemo, useRef, useEffect } from 'react';
import { 
  Calendar, Clock, DollarSign, MapPin, Filter, Search, Grid, List, Plus, 
  ChevronUp, ChevronLeft, ChevronRight, AlertCircle, Loader, Copy, CheckCircle
} from 'lucide-react';

// Import real API functions
import { gymsApi, eventsApi } from '../lib/api';

// Exact Color Theme from user's specification
const theme = {
  colors: {
    primary: '#b48f8f',        // Blush pink - primary brand highlight
    secondary: '#cec4c1',      // Neutral gray-beige - cards, secondary elements
    accent: '#8f93a0',         // Gray-blue - subtle highlights
    textPrimary: '#4a4a4a',    // Dark gray - main text (enhanced)
    textSecondary: '#8f93a0',  // Medium gray - secondary text (enhanced)
    success: '#22c55e',        // Green - success states
    warning: '#f59e0b',        // Orange - warnings
    error: '#ef4444',          // Red - errors
    background: '#f9fafb',     // Light background (enhanced)
  },
  gradients: {
    background: 'linear-gradient(135deg, #fdf7f7 0%, #f5f1f1 50%, #f0ebeb 100%)',
    card: 'linear-gradient(135deg, rgba(255,255,255,0.9) 0%, rgba(255,255,255,0.7) 100%)',
  }
};

// Special event types that are tracked
const specialEventTypes = [
  "CLINIC",
  "KIDS NIGHT OUT", 
  "OPEN GYM"
];

// Special event page URLs for each gym and event type (for clickable links)
const specialEventPageUrls = {
  "Capital Gymnastics Cedar Park": {
    "ALL": "https://portal.iclasspro.com/capgymavery/camps/17?sortBy=time",
    "CLINIC": "https://portal.iclasspro.com/capgymavery/camps/7?sortBy=time",
    "KIDS NIGHT OUT": "https://portal.iclasspro.com/capgymavery/camps/13?sortBy=time",
    "OPEN GYM": null
  },
  "Capital Gymnastics Pflugerville": {
    "ALL": "https://portal.iclasspro.com/capgymhp/camps/81?sortBy=name",
    "CLINIC": "https://portal.iclasspro.com/capgymhp/camps/31?sortBy=time",
    "KIDS NIGHT OUT": "https://portal.iclasspro.com/capgymhp/camps/2?sortBy=time",
    "OPEN GYM": null
  },
  "Capital Gymnastics Round Rock": {
    "ALL": "https://portal.iclasspro.com/capgymroundrock/camps/35?sortBy=time",
    "CLINIC": "https://portal.iclasspro.com/capgymroundrock/camps/28?sortBy=time",
    "KIDS NIGHT OUT": "https://portal.iclasspro.com/capgymroundrock/camps/26?sortBy=time",
    "OPEN GYM": null
  },
  "Rowland Ballard - Atascocita": {
    "ALL": "https://portal.iclasspro.com/rbatascocita/camps/76?sortBy=name",
    "CLINIC": "https://portal.iclasspro.com/rbatascocita/camps/33?sortBy=time",
    "KIDS NIGHT OUT": "https://portal.iclasspro.com/rbatascocita/camps/35?sortBy=time",
    "OPEN GYM": null
  },
  "Rowland Ballard - Kingwood": {
    "ALL": "https://portal.iclasspro.com/rbkingwood/camps/75?sortBy=name",
    "CLINIC": "https://portal.iclasspro.com/rbkingwood/camps/31?sortBy=time",
    "KIDS NIGHT OUT": "https://portal.iclasspro.com/rbkingwood/camps/34?sortBy=time",
    "OPEN GYM": null
  },
  "Houston Gymnastics Academy": {
    "ALL": "https://portal.iclasspro.com/houstongymnastics/camps/32?sortBy=name",
    "CLINIC": "https://portal.iclasspro.com/houstongymnastics/camps/9?sortBy=time",
    "KIDS NIGHT OUT": "https://portal.iclasspro.com/houstongymnastics/camps/8?sortBy=time",
    "OPEN GYM": null
  },
  "Estrella Gymnastics": {
    "ALL": "https://portal.iclasspro.com/estrellagymnastics/camps/28?sortBy=name",
    "CLINIC": "https://portal.iclasspro.com/estrellagymnastics/camps/7?sortBy=time",
    "KIDS NIGHT OUT": "https://portal.iclasspro.com/estrellagymnastics/camps/6?sortBy=time",
    "OPEN GYM": null
  },
  "Oasis Gymnastics": {
    "ALL": "https://portal.iclasspro.com/oasisgymnastics/camps/29?sortBy=name",
    "CLINIC": "https://portal.iclasspro.com/oasisgymnastics/camps/8?sortBy=time",
    "KIDS NIGHT OUT": "https://portal.iclasspro.com/oasisgymnastics/camps/7?sortBy=time",
    "OPEN GYM": null
  },
  "Scottsdale Gymnastics": {
    "ALL": "https://portal.iclasspro.com/scottsdaleflips/camps/30?sortBy=name",
    "CLINIC": "https://portal.iclasspro.com/scottsdaleflips/camps/9?sortBy=time",
    "KIDS NIGHT OUT": "https://portal.iclasspro.com/scottsdaleflips/camps/8?sortBy=time",
    "OPEN GYM": null
  },
  "Tigar Gymnastics": {
    "ALL": "https://portal.iclasspro.com/tigar/camps/27?sortBy=name",
    "CLINIC": "https://portal.iclasspro.com/tigar/camps/6?sortBy=time",
    "KIDS NIGHT OUT": "https://portal.iclasspro.com/tigar/camps/5?sortBy=time",
    "OPEN GYM": null
  }
};

// Custom hooks
const useGyms = () => {
  const [gyms, setGyms] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchGyms = async () => {
      try {
        const data = await gymsApi.getAll();
        setGyms(data || []);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchGyms();
  }, []);

  return { gyms, loading, error };
};

const useEvents = (startDate, endDate) => {
  const [events, setEvents] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchEvents = async () => {
      try {
        setLoading(true);
        const data = await eventsApi.getAll(startDate, endDate);
        setEvents(data || []);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    if (startDate && endDate) {
      fetchEvents();
    }
  }, [startDate, endDate]);

  return { events, loading, error, refetch: () => {
    // Re-fetch logic would go here
  }};
};

const EventsDashboard = () => {
  // State management
  const [selectedGym, setSelectedGym] = useState('all');
  const [selectedEventType, setSelectedEventType] = useState('all');
  const [searchTerm, setSearchTerm] = useState('');
  const [viewMode, setViewMode] = useState('calendar');
  const [currentMonth, setCurrentMonth] = useState(6); // July (0-indexed)
  const [currentYear, setCurrentYear] = useState(2025);
  const [calendarView, setCalendarView] = useState('firstHalf'); // Start with 1-15
  const [hoveredEvent, setHoveredEvent] = useState(null);
  const [showBackToTop, setShowBackToTop] = useState(false);
  const [copiedUrl, setCopiedUrl] = useState(null);

  // Refs
  const topRef = useRef(null);
  const calendarRef = useRef(null);
  const gymRefs = useRef({});

  // Fetch data
  const { gyms: gymsList, loading: gymsLoading, error: gymsError } = useGyms();

  const startDate = `${currentYear}-${String(currentMonth + 1).padStart(2, '0')}-01`;
  const endDate = new Date(currentYear, currentMonth + 1, 0).toISOString().split('T')[0];
  const { events, loading: eventsLoading, error: eventsError } = useEvents(startDate, endDate);

  // Helper functions
  const getDaysInMonth = (year, month) => {
    return new Date(year, month + 1, 0).getDate();
  };

  const daysInCurrentMonth = getDaysInMonth(currentYear, currentMonth);

  const displayDates = useMemo(() => {
    if (calendarView === 'firstHalf') {
      return Array.from({ length: 15 }, (_, i) => i + 1);
    } else {
      return Array.from({ length: daysInCurrentMonth - 15 }, (_, i) => i + 16);
    }
  }, [calendarView, daysInCurrentMonth]);

  const goToPreviousMonth = () => {
    if (currentMonth === 0) {
      setCurrentMonth(11);
      setCurrentYear(currentYear - 1);
    } else {
      setCurrentMonth(currentMonth - 1);
    }
    setCalendarView('firstHalf');
  };

  const goToNextMonth = () => {
    if (currentMonth === 11) {
      setCurrentMonth(0);
      setCurrentYear(currentYear + 1);
    } else {
      setCurrentMonth(currentMonth + 1);
    }
    setCalendarView('firstHalf');
  };

  const scrollToGym = (gym) => {
    const gymElement = gymRefs.current[gym];
    if (gymElement) {
      gymElement.scrollIntoView({ 
        behavior: 'smooth', 
        block: 'center' 
      });
      // Add a visual highlight effect
      gymElement.style.backgroundColor = theme.colors.secondary;
      setTimeout(() => {
        gymElement.style.backgroundColor = '';
      }, 2000);
    }
  };

  const scrollToTop = () => {
    topRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  // Scroll listener for back to top button
  useEffect(() => {
    const handleScroll = () => {
      setShowBackToTop(window.scrollY > 400);
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  // Data processing
  const eventTypes = useMemo(() => {
    const types = [...new Set(events.map(event => event.event_type))];
    return types.filter(Boolean).sort();
  }, [events]);

  const filteredEvents = useMemo(() => {
    return events.filter(event => {
      const matchesGym = selectedGym === 'all' || event.gym_code === selectedGym;
      const matchesType = selectedEventType === 'all' || event.event_type === selectedEventType;
      const matchesSearch = searchTerm === '' || 
        event.title?.toLowerCase().includes(searchTerm.toLowerCase()) ||
        event.event_type?.toLowerCase().includes(searchTerm.toLowerCase());

      return matchesGym && matchesType && matchesSearch;
    });
  }, [events, selectedGym, selectedEventType, searchTerm]);

  const uniqueGymsWithEvents = useMemo(() => {
    const gymCodes = [...new Set(filteredEvents.map(event => event.gym_code))];
    return gymCodes.sort();
  }, [filteredEvents]);

  // Get unique gym names from events
  const allGyms = useMemo(() => [...new Set(filteredEvents.map(event => event.gym_name || event.gym_code).filter(Boolean))], [filteredEvents]);

  // Statistics calculation functions
  const getEventCounts = () => {
    const counts = {};

    allGyms.forEach(gym => {
      counts[gym] = {};
      specialEventTypes.forEach(type => {
        counts[gym][type] = filteredEvents.filter(
          event => (event.gym_name || event.gym_code) === gym && event.event_type === type
        ).length;
      });

      counts[gym].total = filteredEvents.filter(
        event => (event.gym_name || event.gym_code) === gym && specialEventTypes.includes(event.event_type)
      ).length;
    });

    return counts;
  };

  const hasEventType = (gym, type) => {
    return filteredEvents.some(event => (event.gym_name || event.gym_code) === gym && event.event_type === type);
  };

  const getMissingEventTypes = (gym) => {
    return specialEventTypes.filter(type => !hasEventType(gym, type));
  };

  const getEventTypeColor = (eventType) => {
    const colors = {
      'CLINIC': '#8b5cf6',
      'KIDS NIGHT OUT': '#06b6d4',
      'OPEN GYM': '#10b981',
      'SUMMER CAMP': '#f59e0b',
      'BIRTHDAY PARTY': '#ec4899',
      'SPECIAL EVENT': '#ef4444',
      'WORKSHOP': '#6366f1',
    };
    return colors[eventType] || theme.colors.accent;
  };

  const copyToClipboard = async (text, eventId) => {
    try {
      await navigator.clipboard.writeText(text);
      setCopiedUrl(eventId);
      setTimeout(() => setCopiedUrl(null), 2000);
    } catch (err) {
      console.error('Failed to copy:', err);
    }
  };

  // Loading states
  if (gymsLoading || eventsLoading) {
    return (
      <div className="flex items-center justify-center min-h-screen" style={{ backgroundColor: theme.colors.background }}>
        <div className="text-center">
          <Loader className="w-8 h-8 animate-spin mx-auto mb-4" style={{ color: theme.colors.primary }} />
          <p style={{ color: theme.colors.textSecondary }}>Loading events data...</p>
        </div>
      </div>
    );
  }

  if (gymsError || eventsError) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="text-center text-red-600">
          <AlertCircle className="w-8 h-8 mx-auto mb-4" />
          <p>Error loading data: {gymsError || eventsError}</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen" style={{ backgroundColor: theme.colors.background }}>
      <div ref={topRef} className="relative z-10 p-4">
        <div className="max-w-full mx-auto px-2">
          {/* Dashboard Header */}
          <div className="text-center mb-6">
            <h1 className="text-4xl font-bold bg-gradient-to-r from-pink-400 via-purple-400 to-indigo-400 bg-clip-text text-transparent mb-2">
              ✨ Master Events Hub ✨
            </h1>
            <p className="text-gray-600">Managing events across 10 gymnastics facilities</p>
          </div>

          {/* Dashboard Stats Cards */}
          <div className="grid grid-cols-1 md:grid-cols-5 gap-4 mb-8">
            <div className="bg-white rounded-lg shadow-lg p-4 border-l-4" style={{ borderColor: theme.colors.primary }}>
              <div className="text-3xl font-bold" style={{ color: theme.colors.textPrimary }}>
                {filteredEvents.length}
              </div>
              <div className="text-sm" style={{ color: theme.colors.textSecondary }}>
                Total Events
              </div>
              <div className="text-xs" style={{ color: theme.colors.textSecondary }}>
                This Month
              </div>
            </div>

            <div className="bg-white rounded-lg shadow-lg p-4 border-l-4" style={{ borderColor: theme.colors.accent }}>
              <div className="text-3xl font-bold" style={{ color: theme.colors.textPrimary }}>
                {uniqueGymsWithEvents.length}
              </div>
              <div className="text-sm" style={{ color: theme.colors.textSecondary }}>
                Active Gyms
              </div>
            </div>

            <div className="bg-white rounded-lg shadow-lg p-4 border-l-4" style={{ borderColor: theme.colors.success }}>
              <div className="text-3xl font-bold" style={{ color: theme.colors.textPrimary }}>
                {eventTypes.length}
              </div>
              <div className="text-sm" style={{ color: theme.colors.textSecondary }}>
                Event Types
              </div>
            </div>

            <div className="bg-white rounded-lg shadow-lg p-4 border-l-4" style={{ borderColor: theme.colors.warning }}>
              <div className="text-3xl font-bold" style={{ color: theme.colors.textPrimary }}>
                {filteredEvents.filter(e => e.event_type === 'CLINIC').length}
              </div>
              <div className="text-sm" style={{ color: theme.colors.textSecondary }}>
                Clinics
              </div>
            </div>

            <div className="bg-white rounded-lg shadow-lg p-4 border-l-4" style={{ borderColor: theme.colors.error }}>
              <div className="text-3xl font-bold" style={{ color: theme.colors.textPrimary }}>
                {filteredEvents.filter(e => e.event_type === 'SUMMER CAMP').length}
              </div>
              <div className="text-sm" style={{ color: theme.colors.textSecondary }}>
                Summer Camps
              </div>
            </div>
          </div>

          {/* Special Event Statistics by Gym */}
          <div className="bg-white rounded-lg shadow-lg p-6 mb-8" style={{ borderColor: '#cec4c1', borderWidth: '1px' }}>
            <div className="flex items-center justify-between mb-4">
              <h2 className="text-2xl font-bold" style={{ color: theme.colors.textPrimary }}>
                Special Event Statistics by Gym
                <span className="text-sm font-normal ml-2" style={{ color: theme.colors.textSecondary }}>
                  (Click counts to view event pages)
                </span>
              </h2>
              <div className="text-sm" style={{ color: theme.colors.textSecondary }}>
                {new Date(currentYear, currentMonth).toLocaleDateString('en-US', { 
                  month: 'long', 
                  year: 'numeric' 
                })}
              </div>
            </div>

            <div className="grid grid-cols-2 md:grid-cols-5 gap-3 mb-6">
              {allGyms.map(gym => {
                const eventCount = filteredEvents.filter(e => (e.gym_name || e.gym_code) === gym).length;

                return (
                  <button
                    key={gym}
                    onClick={() => scrollToGym(gym)}
                    className="px-3 py-2 rounded-full text-white text-sm font-medium transition-all duration-150 hover:scale-105 hover:shadow-md flex items-center justify-center gap-2"
                    style={{ backgroundColor: theme.colors.primary }}
                  >
                    {gym}
                    <span className="bg-white bg-opacity-30 px-2 py-0.5 rounded-full text-xs">
                      {eventCount}
                    </span>
                  </button>
                );
              })}
            </div>

            <div className="overflow-x-auto">
              <table className="min-w-full border border-gray-200">
                <thead>
                  <tr className="bg-gray-100">
                    <th className="p-2 border" style={{ color: theme.colors.textPrimary }}>Gym</th>
                    {specialEventTypes.map((type, i) => (
                      <th key={i} className="p-2 border" style={{ color: theme.colors.textPrimary }}>{type}</th>
                    ))}
                    <th className="p-2 border" style={{ color: theme.colors.textPrimary }}>Total Tracked</th>
                    <th className="p-2 border" style={{ color: theme.colors.textPrimary }}>Missing Required</th>
                    <th className="p-2 border text-xs relative group cursor-help" style={{ color: theme.colors.textSecondary }}>
                      <div className="flex items-center justify-center gap-1">
