                    </th>
                  </tr>
                </thead>
                <tbody>
                  {allGyms.map((gym, i) => {
                    const counts = getEventCounts();
                    const summerCamps = filteredEvents.filter(event => 
                      (event.gym_name || event.gym_code) === gym && 
                      event.event_type?.startsWith('SUMMER CAMP')
