import React, { useState, useMemo, useRef, useEffect } from 'react';
import { 
  Calendar, Clock, DollarSign, MapPin, Filter, Search, Grid, List, Plus, 
  ChevronUp, ChevronLeft, ChevronRight, AlertCircle, Loader, Copy, CheckCircle,
  PanelLeftClose, PanelLeftOpen
} from 'lucide-react';

// Import real API functions
import { gymsApi, eventsApi } from '../lib/api';
import { gymLinksApi } from '../lib/gymLinksApi';

// Exact Color Theme from user's specification
const theme = {
  colors: {
    primary: '#b48f8f',        // Blush pink - primary brand highlight
    secondary: '#cec4c1',      // Neutral gray-beige - cards, secondary elements
    accent: '#8f93a0',         // Gray-blue - subtle highlights
    textPrimary: '#4a4a4a',    // Dark gray - main text (enhanced)
    textSecondary: '#8f93a0',  // Medium gray - secondary text (enhanced)
    success: '#22c55e',        // Green - success states
    warning: '#f59e0b',        // Orange - warnings
    error: '#ef4444',          // Red - errors
    background: '#f9fafb',     // Light background (enhanced)
  },
  gradients: {
    background: 'linear-gradient(135deg, #fdf7f7 0%, #f5f1f1 50%, #f0ebeb 100%)',
    card: 'linear-gradient(135deg, rgba(255,255,255,0.9) 0%, rgba(255,255,255,0.7) 100%)',
  }
};

// Special event types that are tracked with minimum requirements
const specialEventTypes = [
  "CLINIC",
  "KIDS NIGHT OUT", 
  "OPEN GYM"
];

// Minimum requirements per gym
const minimumRequirements = {
  "CLINIC": 1,
  "KIDS NIGHT OUT": 2,
  "OPEN GYM": 1
};

// Note: Special event URLs now loaded from Supabase LinkFactory database

// Custom hooks
const useGyms = () => {
  const [gyms, setGyms] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchGyms = async () => {
      try {
        const data = await gymsApi.getAll();
        setGyms(data || []);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchGyms();
  }, []);

  return { gyms, loading, error };
};

const useGymLinks = () => {
  const [gymLinks, setGymLinks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchGymLinks = async () => {
      try {
        const data = await gymLinksApi.getAllLinksDetailed();
        setGymLinks(data || []);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchGymLinks();
  }, []);

  return { gymLinks, loading, error };
};

const useEvents = (startDate, endDate) => {
  const [events, setEvents] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchEvents = async () => {
      try {
        setLoading(true);
        const data = await eventsApi.getAll(startDate, endDate);
        setEvents(data || []);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    if (startDate && endDate) {
      fetchEvents();
    }
  }, [startDate, endDate]);

  return { events, loading, error, refetch: () => {
    // Re-fetch logic would go here
  }};
};

const EventsDashboard = ({ onBackToHub }) => {
  // State management
  const [selectedGym, setSelectedGym] = useState('all');
  const [selectedEventType, setSelectedEventType] = useState('all');
  const [searchTerm, setSearchTerm] = useState('');
  const [viewMode, setViewMode] = useState('calendar');
  // Set to current date instead of July 2025
  const now = new Date();
  const [currentMonth, setCurrentMonth] = useState(now.getMonth()); // Current month (0-indexed)  
  const [currentYear, setCurrentYear] = useState(now.getFullYear()); // Current year
  const [calendarView, setCalendarView] = useState('firstHalf'); // Start with 1-15
  const [hoveredEvent, setHoveredEvent] = useState(null);
  const [showBackToTop, setShowBackToTop] = useState(false);
  const [copiedUrl, setCopiedUrl] = useState(null);
  const [copySuccess, setCopySuccess] = useState('');
  const [showCalendarNav, setShowCalendarNav] = useState(true);
  const [isInCalendarView, setIsInCalendarView] = useState(false);

  // Refs
  const topRef = useRef(null);
  const calendarRef = useRef(null);
  const gymRefs = useRef({});

  // Fetch data
  const { gyms: gymsList, loading: gymsLoading, error: gymsError } = useGyms();
  const { gymLinks, loading: gymLinksLoading, error: gymLinksError } = useGymLinks();

  const startDate = `${currentYear}-${String(currentMonth + 1).padStart(2, '0')}-01`;
  const endDate = new Date(currentYear, currentMonth + 1, 0).toISOString().split('T')[0];
  const { events, loading: eventsLoading, error: eventsError } = useEvents(startDate, endDate);

  // Helper function to get URLs from real Supabase data
  const getGymLinkUrl = (gymName, linkTypeLabel) => {
    const link = gymLinks.find(gl => 
      gl.gym_name === gymName && 
      (gl.link_type_label === linkTypeLabel || gl.link_type_name === linkTypeLabel)
    );
    return link?.url;
  };

  // Helper function to get all URLs for a specific link type
  const getAllUrlsForLinkType = (linkTypeLabel) => {
    return gymLinks
      .filter(gl => gl.link_type_label === linkTypeLabel || gl.link_type_name === linkTypeLabel)
      .map(gl => gl.url)
      .filter(url => url);
  };

  // Helper function to map event types to link type labels
  const getEventTypeLinkLabel = (eventType) => {
    const mapping = {
      'CLINIC': 'Skill Clinics',
      'KIDS NIGHT OUT': 'Kids Night Out',
      'OPEN GYM': 'Open Gym'
    };
    return mapping[eventType] || eventType;
  };

  // Helper functions
  const getDaysInMonth = (year, month) => {
    return new Date(year, month + 1, 0).getDate();
  };

  // Removed unused variable - using getDaysInMonth() directly where needed

  const displayDates = useMemo(() => {
    const totalDays = getDaysInMonth(currentYear, currentMonth);

    switch (calendarView) {
      case 'firstHalf':
        return Array.from({ length: 15 }, (_, i) => i + 1);
      case 'secondHalf':
        return Array.from({ length: totalDays - 15 }, (_, i) => i + 16);
      case 'full':
        return Array.from({ length: totalDays }, (_, i) => i + 1);
      case 'week1':
        return Array.from({ length: 7 }, (_, i) => i + 1);
      case 'week2':
        return Array.from({ length: 7 }, (_, i) => i + 8);
      case 'week3':
        return Array.from({ length: 7 }, (_, i) => i + 15);
      case 'week4':
        return Array.from({ length: Math.min(7, totalDays - 21) }, (_, i) => i + 22);
      default:
        return Array.from({ length: 15 }, (_, i) => i + 1);
    }
  }, [calendarView, currentYear, currentMonth]);

  const goToPreviousMonth = () => {
    if (currentMonth === 0) {
      setCurrentMonth(11);
      setCurrentYear(currentYear - 1);
    } else {
      setCurrentMonth(currentMonth - 1);
    }
    setCalendarView('firstHalf');
  };

  const goToNextMonth = () => {
    if (currentMonth === 11) {
      setCurrentMonth(0);
      setCurrentYear(currentYear + 1);
    } else {
      setCurrentMonth(currentMonth + 1);
    }
    setCalendarView('firstHalf');
  };

  const scrollToGym = (gym) => {
    const gymElement = gymRefs.current[gym];
    if (gymElement) {
      gymElement.scrollIntoView({ 
        behavior: 'smooth', 
        block: 'center' 
      });
      // Add a visual highlight effect
      gymElement.style.backgroundColor = theme.colors.secondary;
      setTimeout(() => {
        gymElement.style.backgroundColor = '';
      }, 2000);
    }
  };

  const scrollToTop = () => {
    topRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  // Calendar view change handler
  const handleCalendarViewChange = (newView) => {
    setCalendarView(newView);
  };

  // Scroll listener for back to top button and calendar view detection
  useEffect(() => {
    const handleScroll = () => {
      const scrollY = window.scrollY;
      setShowBackToTop(scrollY > 400);

      // Detect if we're in the calendar view area
      if (calendarRef.current) {
        const calendarRect = calendarRef.current.getBoundingClientRect();
        const windowHeight = window.innerHeight;
        const inCalendarView = calendarRect.top < windowHeight * 0.7 && calendarRect.bottom > windowHeight * 0.3;
        setIsInCalendarView(inCalendarView);
      }
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  // Data processing
  const eventTypes = useMemo(() => {
    const types = [...new Set(events.map(event => event.type))];
    return types.filter(Boolean).sort();
  }, [events]);

  const filteredEvents = useMemo(() => {
    return events.filter(event => {
      const matchesGym = selectedGym === 'all' || event.gym_code === selectedGym;
      const matchesType = selectedEventType === 'all' || event.type === selectedEventType;
      const matchesSearch = searchTerm === '' || 
        event.title?.toLowerCase().includes(searchTerm.toLowerCase()) ||
        event.type?.toLowerCase().includes(searchTerm.toLowerCase());

      return matchesGym && matchesType && matchesSearch;
    });
  }, [events, selectedGym, selectedEventType, searchTerm]);

  const uniqueGymsWithEvents = useMemo(() => {
    const gymCodes = [...new Set(filteredEvents.map(event => event.gym_code))];
    return gymCodes.sort();
  }, [filteredEvents]);

  // Get all gyms (from gymsList) for calendar display, or gyms with events for statistics
  const allGymsFromList = useMemo(() => gymsList.map(gym => gym.name).sort(), [gymsList]);
  const allGyms = useMemo(() => {
    const gymsWithEvents = [...new Set(filteredEvents.map(event => event.gym_name || event.gym_code).filter(Boolean))];
    // For calendar display, show all gyms; for statistics, show only gyms with events
    return allGymsFromList.length > 0 ? allGymsFromList : gymsWithEvents;
  }, [filteredEvents, allGymsFromList]);

  // Debug logging
  console.log('Debug Info:', {
    totalEvents: events.length,
    filteredEvents: filteredEvents.length,
    allGyms: allGyms,
    currentMonth: currentMonth + 1, // +1 because JS months are 0-indexed
    currentYear: currentYear,
    startDate,
    endDate,
    sampleEvent: events[0],
    monthName: new Date(currentYear, currentMonth).toLocaleDateString('en-US', { month: 'long', year: 'numeric' })
  });

  // Statistics calculation functions
  const getEventCounts = () => {
    const counts = {};

    allGyms.forEach(gym => {
      counts[gym] = {};
      specialEventTypes.forEach(type => {
        counts[gym][type] = filteredEvents.filter(
          event => (event.gym_name || event.gym_code) === gym && event.type === type
        ).length;
      });

      counts[gym].total = filteredEvents.filter(
        event => (event.gym_name || event.gym_code) === gym && specialEventTypes.includes(event.type)
      ).length;
    });

    return counts;
  };

  const hasEventType = (gym, type) => {
    return filteredEvents.some(event => (event.gym_name || event.gym_code) === gym && event.type === type);
  };

  const getMissingEventTypes = (gym) => {
    const missing = [];
    const counts = getEventCounts();

    specialEventTypes.forEach(type => {
      const currentCount = counts[gym]?.[type] || 0;
      const requiredCount = minimumRequirements[type];
      const deficit = requiredCount - currentCount;

      if (deficit > 0) {
        if (deficit === 1) {
          missing.push(`Need 1 ${type.toLowerCase()}`);
        } else {
          missing.push(`Need ${deficit} ${type.toLowerCase()}s`);
        }
      }
    });

    return missing;
  };

  const getEventTypeColor = (eventType) => {
    const colors = {
      // Required Events (Tracked) - Improved colors
      'CLINIC': '#F3E8FF',                    // Light lavender (kept - it was already good)
