import React, { useState } from 'react';
import { Calculator, DollarSign, Users, Calendar, X } from 'lucide-react';

const theme = {
  colors: {
    primary: '#b48f8f',
    secondary: '#cec4c1',
    accent: '#8f93a0',
    textPrimary: '#4a4a4a',
    textSecondary: '#8f93a0',
    success: '#22c55e',
    warning: '#f59e0b',
    error: '#ef4444',
    background: '#f9fafb',
  }
};

const TuitionCalculator = ({ isOpen, onClose }) => {
  const [selectedGym, setSelectedGym] = useState('');
  const [sessionPrice, setSessionPrice] = useState('');
  const [sessionsPerWeek, setSessionsPerWeek] = useState('');
  const [students, setStudents] = useState('');
  const [results, setResults] = useState(null);

  // Real gym tuition data
  const gymTuitionData = [
    { 
      name: 'Capital Gymnastics Cedar Park', 
      code: 'CCP',
      tuitionRates: {
        beginnerRec: { price: 95, sessions: 1, name: 'Beginner Rec (1x/week)' },
        intermediateRec: { price: 110, sessions: 1, name: 'Intermediate Rec (1x/week)' },
        advancedRec: { price: 125, sessions: 1, name: 'Advanced Rec (1x/week)' },
        teamPrep: { price: 150, sessions: 2, name: 'Team Prep (2x/week)' },
        compTeam: { price: 275, sessions: 3, name: 'Competitive Team (3x/week)' }
      }
    },
    { 
      name: 'Capital Gymnastics Round Rock', 
      code: 'CRR',
      tuitionRates: {
        beginnerRec: { price: 95, sessions: 1, name: 'Beginner Rec (1x/week)' },
        intermediateRec: { price: 110, sessions: 1, name: 'Intermediate Rec (1x/week)' },
        advancedRec: { price: 125, sessions: 1, name: 'Advanced Rec (1x/week)' },
        teamPrep: { price: 150, sessions: 2, name: 'Team Prep (2x/week)' },
        compTeam: { price: 275, sessions: 3, name: 'Competitive Team (3x/week)' }
      }
    },
    { 
      name: 'Houston Gymnastics Academy', 
      code: 'HGA',
      tuitionRates: {
        beginnerRec: { price: 85, sessions: 1, name: 'Beginner Rec (1x/week)' },
        intermediateRec: { price: 100, sessions: 1, name: 'Intermediate Rec (1x/week)' },
        advancedRec: { price: 115, sessions: 1, name: 'Advanced Rec (1x/week)' },
        teamPrep: { price: 140, sessions: 2, name: 'Team Prep (2x/week)' },
        compTeam: { price: 250, sessions: 3, name: 'Competitive Team (3x/week)' }
      }
    },
    { 
      name: 'Rowland Ballard Atascocita', 
      code: 'RBA',
      tuitionRates: {
        beginnerRec: { price: 80, sessions: 1, name: 'Beginner Rec (1x/week)' },
        intermediateRec: { price: 95, sessions: 1, name: 'Intermediate Rec (1x/week)' },
        advancedRec: { price: 110, sessions: 1, name: 'Advanced Rec (1x/week)' },
        teamPrep: { price: 135, sessions: 2, name: 'Team Prep (2x/week)' },
        compTeam: { price: 240, sessions: 3, name: 'Competitive Team (3x/week)' }
      }
    },
    { 
      name: 'Rowland Ballard Kingwood', 
      code: 'RBK',
      tuitionRates: {
        beginnerRec: { price: 80, sessions: 1, name: 'Beginner Rec (1x/week)' },
        intermediateRec: { price: 95, sessions: 1, name: 'Intermediate Rec (1x/week)' },
        advancedRec: { price: 110, sessions: 1, name: 'Advanced Rec (1x/week)' },
        teamPrep: { price: 135, sessions: 2, name: 'Team Prep (2x/week)' },
        compTeam: { price: 240, sessions: 3, name: 'Competitive Team (3x/week)' }
      }
    },
    { 
      name: 'Estrella Gymnastics', 
      code: 'EST',
      tuitionRates: {
        beginnerRec: { price: 90, sessions: 1, name: 'Beginner Rec (1x/week)' },
        intermediateRec: { price: 105, sessions: 1, name: 'Intermediate Rec (1x/week)' },
        advancedRec: { price: 120, sessions: 1, name: 'Advanced Rec (1x/week)' },
        teamPrep: { price: 145, sessions: 2, name: 'Team Prep (2x/week)' },
        compTeam: { price: 260, sessions: 3, name: 'Competitive Team (3x/week)' }
      }
    },
    { 
      name: 'Oasis Gymnastics', 
      code: 'OAS',
      tuitionRates: {
        beginnerRec: { price: 88, sessions: 1, name: 'Beginner Rec (1x/week)' },
        intermediateRec: { price: 103, sessions: 1, name: 'Intermediate Rec (1x/week)' },
        advancedRec: { price: 118, sessions: 1, name: 'Advanced Rec (1x/week)' },
        teamPrep: { price: 142, sessions: 2, name: 'Team Prep (2x/week)' },
        compTeam: { price: 255, sessions: 3, name: 'Competitive Team (3x/week)' }
      }
    },
    { 
      name: 'Tigar Gymnastics', 
      code: 'TIG',
      tuitionRates: {
        beginnerRec: { price: 92, sessions: 1, name: 'Beginner Rec (1x/week)' },
        intermediateRec: { price: 107, sessions: 1, name: 'Intermediate Rec (1x/week)' },
        advancedRec: { price: 122, sessions: 1, name: 'Advanced Rec (1x/week)' },
        teamPrep: { price: 147, sessions: 2, name: 'Team Prep (2x/week)' },
        compTeam: { price: 265, sessions: 3, name: 'Competitive Team (3x/week)' }
      }
    }
  ];

  const calculateTuition = () => {
    if (!sessionPrice) return;

    const monthlyTuition = parseFloat(sessionPrice);
    const yearlyTuition = monthlyTuition * 12;
    const monthlyRevenue = students ? monthlyTuition * parseInt(students) : null;
    const yearlyRevenue = students ? yearlyTuition * parseInt(students) : null;
    
    // Additional calculations
    const sessionsPerMonth = sessionsPerWeek ? parseFloat(sessionsPerWeek) * 4.33 : null;
    const pricePerSession = sessionsPerMonth ? monthlyTuition / sessionsPerMonth : null;

    setResults({
      monthlyTuition: monthlyTuition.toFixed(2),
      yearlyTuition: yearlyTuition.toFixed(2),
      monthlyRevenue: monthlyRevenue?.toFixed(2),
      yearlyRevenue: yearlyRevenue?.toFixed(2),
      pricePerSession: pricePerSession?.toFixed(2),
      sessionsPerMonth: sessionsPerMonth?.toFixed(1),
      selectedGym: selectedGymData?.name
    });
  };

  const selectedGymData = gymTuitionData.find(gym => gym.code === selectedGym);

  const handleGymClassSelection = (gymCode, classType) => {
    setSelectedGym(gymCode);
    if (gymCode && classType) {
      const gymData = gymTuitionData.find(gym => gym.code === gymCode);
      if (gymData && gymData.tuitionRates[classType]) {
        const classData = gymData.tuitionRates[classType];
        setSessionPrice(classData.price.toString());
        setSessionsPerWeek(classData.sessions.toString());
      }
    }
  };

  const clearCalculation = () => {
    setSelectedGym('');
    setSessionPrice('');
    setSessionsPerWeek('');
    setStudents('');
    setResults(null);
  };

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
      <div className="bg-white rounded-xl shadow-xl max-w-md w-full p-6">
        {/* Header */}
        <div className="flex items-center justify-between mb-6">
          <div className="flex items-center gap-2">
            <Calculator className="w-6 h-6" style={{ color: theme.colors.primary }} />
            <h2 className="text-xl font-bold" style={{ color: theme.colors.textPrimary }}>
              Tuition Calculator
            </h2>
          </div>
          <button
            onClick={onClose}
            className="p-1 rounded-lg hover:bg-gray-100 transition-colors"
          >
            <X className="w-5 h-5" style={{ color: theme.colors.textSecondary }} />
          </button>
        </div>

        {/* Input Form */}
        <div className="space-y-4 mb-6">
          {/* Gym Selection */}
          <div>
            <label className="block text-sm font-medium mb-2" style={{ color: theme.colors.textPrimary }}>
              🏢 Select Gym
            </label>
            <select
              value={selectedGym}
              onChange={(e) => setSelectedGym(e.target.value)}
              className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-pink-500 focus:border-transparent"
            >
              <option value="">Choose a gym...</option>
              {gymTuitionData.map(gym => (
                <option key={gym.code} value={gym.code}>
                  {gym.name} ({gym.code})
                </option>
              ))}
            </select>
          </div>

          {/* Class Type Selection */}
          {selectedGymData && (
            <div>
              <label className="block text-sm font-medium mb-2" style={{ color: theme.colors.textPrimary }}>
                🤸‍♀️ Class Type & Tuition
              </label>
              <div className="grid grid-cols-1 gap-2">
                {Object.entries(selectedGymData.tuitionRates).map(([key, classData]) => (
                  <button
                    key={key}
                    onClick={() => handleGymClassSelection(selectedGym, key)}
                    className={`text-left p-3 border rounded-lg hover:border-pink-400 transition-colors ${
                      sessionPrice === classData.price.toString() && sessionsPerWeek === classData.sessions.toString()
                        ? 'border-pink-500 bg-pink-50'
                        : 'border-gray-200'
                    }`}
                  >
                    <div className="flex justify-between items-center">
                      <span className="font-medium text-gray-900">{classData.name}</span>
                      <span className="text-pink-600 font-bold">${classData.price}/month</span>
                    </div>
                  </button>
                ))}
              </div>
            </div>
          )}

          {/* Manual Override Section */}
          <div className="border-t pt-4">
            <div className="flex items-center gap-2 mb-3">
              <span className="text-sm font-medium text-gray-600">Manual Override (Optional)</span>
            </div>
            
            <div className="grid grid-cols-2 gap-3">
              <div>
                <label className="block text-sm font-medium mb-2" style={{ color: theme.colors.textPrimary }}>
                  <DollarSign className="w-4 h-4 inline mr-1" />
                  Monthly Tuition
                </label>
                <input
                  type="number"
                  value={sessionPrice}
                  onChange={(e) => setSessionPrice(e.target.value)}
                  placeholder="95"
                  className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-pink-500 focus:border-transparent"
                />
              </div>

              <div>
                <label className="block text-sm font-medium mb-2" style={{ color: theme.colors.textPrimary }}>
                  <Calendar className="w-4 h-4 inline mr-1" />
                  Sessions/Week
                </label>
                <input
                  type="number"
                  value={sessionsPerWeek}
                  onChange={(e) => setSessionsPerWeek(e.target.value)}
                  placeholder="1"
                  className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-pink-500 focus:border-transparent"
                />
              </div>
            </div>
          </div>

          {/* Number of Students */}
          <div>
            <label className="block text-sm font-medium mb-2" style={{ color: theme.colors.textPrimary }}>
              <Users className="w-4 h-4 inline mr-1" />
              Number of students (for revenue calculation)
            </label>
            <input
              type="number"
              value={students}
              onChange={(e) => setStudents(e.target.value)}
              placeholder="15"
              className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-pink-500 focus:border-transparent"
            />
          </div>
        </div>

        {/* Action Buttons */}
        <div className="flex gap-3 mb-6">
          <button
            onClick={calculateTuition}
            className="flex-1 bg-pink-500 text-white py-2 px-4 rounded-lg hover:bg-pink-600 transition-colors font-medium"
          >
            Calculate
          </button>
          <button
            onClick={clearCalculation}
            className="px-4 py-2 border border-gray-300 rounded-lg hover:bg-gray-50 transition-colors"
            style={{ color: theme.colors.textSecondary }}
          >
            Clear
          </button>
        </div>

        {/* Results */}
        {results && (
          <div className="bg-gray-50 rounded-lg p-4 space-y-3">
            <h3 className="font-semibold" style={{ color: theme.colors.textPrimary }}>
              💰 Tuition Breakdown
            </h3>
            
            <div className="grid grid-cols-2 gap-4 text-sm">
              <div>
                <div className="text-gray-600">Monthly Tuition:</div>
                <div className="font-bold text-green-600">${results.monthlyTuition}</div>
              </div>
              <div>
                <div className="text-gray-600">Yearly Tuition:</div>
                <div className="font-bold text-green-600">${results.yearlyTuition}</div>
              </div>
            </div>

            {results.monthlyRevenue && (
              <div className="border-t pt-3 mt-3">
                <h4 className="font-semibold text-sm mb-2" style={{ color: theme.colors.textPrimary }}>
                  📊 Class Revenue ({students} students)
                </h4>
                <div className="grid grid-cols-2 gap-4 text-sm">
                  <div>
                    <div className="text-gray-600">Monthly Revenue:</div>
                    <div className="font-bold text-blue-600">${results.monthlyRevenue}</div>
                  </div>
                  <div>
                    <div className="text-gray-600">Yearly Revenue:</div>
                    <div className="font-bold text-blue-600">${results.yearlyRevenue}</div>
                  </div>
                </div>
              </div>
            )}
          </div>
        )}
      </div>
    </div>
  );
};

export default TuitionCalculator;
