import React, { useState, useMemo, useRef, useEffect } from 'react';
import { 
  Calendar, Clock, DollarSign, MapPin, Phone, ExternalLink, Filter, Search, Download, Copy, 
  CheckCircle, Grid, List, SortAsc, SortDesc, CheckSquare, Square, Plus, Trash2, CalendarDays, 
  ChevronUp, ChevronLeft, ChevronRight, ChevronDown, Edit, Save, X, Map, Eye, PanelLeftClose, 
  PanelLeftOpen, Sun, AlertCircle, Loader
} from 'lucide-react';

// Import real API functions
import { gymsApi, eventsApi } from '../lib/api';

// Custom hooks
function useGyms() {
  const [gyms, setGyms] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchGyms();
  }, []);

  async function fetchGyms() {
    try {
      setLoading(true);
      const data = await gymsApi.getAll();
      setGyms(data);
      setError(null);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  }

  return { gyms, loading, error, refetch: fetchGyms };
}

function useEvents(startDate, endDate) {
  const [events, setEvents] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchEvents();
  }, [startDate, endDate]);

  async function fetchEvents() {
    try {
      setLoading(true);
      const data = await eventsApi.getAll(startDate, endDate);
      setEvents(data);
      setError(null);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  }

  async function createEvent(eventData) {
    try {
      const newEvent = await eventsApi.create(eventData);
      setEvents(prev => [...prev, newEvent]);
      return { data: newEvent, error: null };
    } catch (err) {
      return { data: null, error: err.message };
    }
  }

  async function updateEvent(id, updates) {
    try {
      const updated = await eventsApi.update(id, updates);
      setEvents(prev => prev.map(e => e.id === id ? updated : e));
      return { data: updated, error: null };
    } catch (err) {
      return { data: null, error: err.message };
    }
  }

  async function deleteEvent(id) {
    try {
      await eventsApi.delete(id);
      setEvents(prev => prev.filter(e => e.id !== id));
      return { error: null };
    } catch (err) {
      return { error: err.message };
    }
  }

  return { 
    events, 
    loading, 
    error, 
    refetch: fetchEvents,
    createEvent,
    updateEvent,
    deleteEvent 
  };
}

const EnhancedJuneEventsDashboard = () => {
  // Date calculations
  const getDefaultCalendarView = () => {
    const today = new Date();
    const currentDay = today.getDate();
    const currentMonth = today.getMonth();
    
    if (currentMonth !== 5 || today.getFullYear() !== 2025) {
      return 'firstHalf';
    }
    
    return currentDay > 15 ? 'secondHalf' : 'firstHalf';
  };

  // State management
  const [selectedGym, setSelectedGym] = useState('all');
  const [selectedEventType, setSelectedEventType] = useState('all');
  const [searchTerm, setSearchTerm] = useState('');
  const [viewMode, setViewMode] = useState('calendar');
  const [sortBy, setSortBy] = useState('date');
  const [sortOrder, setSortOrder] = useState('asc');
  const [showAdminPanel, setShowAdminPanel] = useState(false);
  const [selectedEvents, setSelectedEvents] = useState(new Set());
  const [copiedStates, setCopiedStates] = useState({});
  const [showBackToTop, setShowBackToTop] = useState(false);
  const [showJumpToGym, setShowJumpToGym] = useState(true);
  const [highlightedGym, setHighlightedGym] = useState(null);
  const [calendarView, setCalendarView] = useState(getDefaultCalendarView());
  const [currentDateRange, setCurrentDateRange] = useState(() => {
    const defaultView = getDefaultCalendarView();
    return defaultView === 'secondHalf' ? { start: 16, end: 30 } : { start: 1, end: 15 };
  });
  const [showCalendarNav, setShowCalendarNav] = useState(true);
  const [isInCalendarView, setIsInCalendarView] = useState(false);
  
  // Event management states
  const [showAddForm, setShowAddForm] = useState(false);
  const [editingEvent, setEditingEvent] = useState(null);
  const [newEvent, setNewEvent] = useState({
    gym_id: "",
    title: "",
    date: "",
    time: "",
    price: "",
    day_of_week: "",
    type: "",
    event_url: ""
  });
  const [isSubmitting, setIsSubmitting] = useState(false);

  // Refs for scrolling
  const topRef = useRef(null);
  const gymRefs = useRef({});
  const calendarSectionRef = useRef(null);

  // Fetch data from Supabase
  const { gyms, loading: gymsLoading, error: gymsError } = useGyms();
  const { 
    events: eventsData, 
    loading: eventsLoading, 
    error: eventsError,
    createEvent,
    updateEvent,
    deleteEvent 
  } = useEvents('2025-06-01', '2025-06-30');

  // Special event page URLs for each gym and event type
  const specialEventPageUrls = {
    "Capital Gymnastics Cedar Park": {
      "ALL": "https://portal.iclasspro.com/capgymavery/camps/17?sortBy=time",
      "CLINIC": "https://portal.iclasspro.com/capgymavery/camps/7?sortBy=time",
      "KIDS NIGHT OUT": "https://portal.iclasspro.com/capgymavery/camps/13?sortBy=time",
      "OPEN GYM": null
    },
    "Capital Gymnastics Pflugerville": {
      "ALL": "https://portal.iclasspro.com/capgymhp/camps/81?sortBy=name",
      "CLINIC": "https://portal.iclasspro.com/capgymhp/camps/31?sortBy=time",
      "KIDS NIGHT OUT": "https://portal.iclasspro.com/capgymhp/camps/2?sortBy=time",
      "OPEN GYM": null
    },
    "Capital Gymnastics Round Rock": {
      "ALL": "https://portal.iclasspro.com/capgymroundrock/camps/35?sortBy=time",
      "CLINIC": "https://portal.iclasspro.com/capgymroundrock/camps/28?sortBy=time",
      "KIDS NIGHT OUT": "https://portal.iclasspro.com/capgymroundrock/camps/26?sortBy=time",
      "OPEN GYM": null
    },
    "Rowland Ballard - Atascocita": {
      "ALL": "https://portal.iclasspro.com/rbatascocita/camps/76?sortBy=name",
      "CLINIC": "https://portal.iclasspro.com/rbatascocita/camps/33?sortBy=time",
      "KIDS NIGHT OUT": "https://portal.iclasspro.com/rbatascocita/camps/35?sortBy=time",
      "OPEN GYM": null
    },
    "Rowland Ballard - Kingwood": {
      "ALL": "https://portal.iclasspro.com/rbkingwood/camps/6?sortBy=time",
      "CLINIC": "https://portal.iclasspro.com/rbkingwood/camps/31?sortBy=time",
      "KIDS NIGHT OUT": "https://portal.iclasspro.com/rbkingwood/camps/26?sortBy=time",
      "OPEN GYM": null
    },
    "Houston Gymnastics Academy": {
      "ALL": "https://portal.iclasspro.com/houstongymnastics/camps/15?sortBy=time",
      "CLINIC": "https://portal.iclasspro.com/houstongymnastics/camps/2?sortBy=time",
      "KIDS NIGHT OUT": "https://portal.iclasspro.com/houstongymnastics/camps/7?sortBy=time",
      "OPEN GYM": null
    },
    "Estrella Gymnastics": {
      "ALL": "https://portal.iclasspro.com/estrellagymnastics/camps/99?sortBy=time",
      "CLINIC": "https://portal.iclasspro.com/estrellagymnastics/camps/24?sortBy=time",
      "KIDS NIGHT OUT": "https://portal.iclasspro.com/estrellagymnastics/camps/3?sortBy=time",
      "OPEN GYM": null
    },
    "Oasis Gymnastics": {
      "ALL": "https://portal.iclasspro.com/oasisgymnastics/camps/60?sortBy=time",
      "CLINIC": "https://portal.iclasspro.com/oasisgymnastics/camps/33?sortBy=time",
      "KIDS NIGHT OUT": "https://portal.iclasspro.com/oasisgymnastics/camps/27?sortBy=time",
      "OPEN GYM": null
    },
    "Scottsdale Gymnastics": {
      "ALL": "https://portal.iclasspro.com/scottsdalegymnastics/camps/88?sortBy=time",
      "CLINIC": "https://portal.iclasspro.com/scottsdalegymnastics/camps/28?sortBy=time",
      "KIDS NIGHT OUT": "https://portal.iclasspro.com/scottsdalegymnastics/camps/32?sortBy=time",
      "OPEN GYM": null
    },
    "Tigar Gymnastics": {
      "ALL": "https://portal.iclasspro.com/tigar/camps/22?sortBy=name",
      "CLINIC": "https://portal.iclasspro.com/tigar/camps/2?sortBy=time",
      "KIDS NIGHT OUT": "https://portal.iclasspro.com/tigar/camps/8?sortBy=time",
      "OPEN GYM": null
    }
  };

  // Get all gym names from the gyms data
  const allGyms = useMemo(() => {
    return gyms.map(gym => gym.name).sort();
  }, [gyms]);

  // Event type colors and configuration
  const eventTypeColors = {
    'CLINIC': 'bg-purple-100 text-purple-800 border-purple-200',
    'KIDS NIGHT OUT': 'bg-orange-100 text-orange-800 border-orange-200',
    'OPEN GYM': 'bg-green-100 text-green-800 border-green-200',
    'SUMMER CAMP - FULL DAY': 'bg-blue-100 text-blue-800 border-blue-200',
    'SUMMER CAMP - GYMNASTICS': 'bg-cyan-100 text-cyan-800 border-cyan-200',
    'SUMMER CAMP - NINJA': 'bg-emerald-100 text-emerald-800 border-emerald-200'
  };

  const getEventTypeColor = (type) => {
    return eventTypeColors[type] || 'bg-gray-100 text-gray-800 border-gray-200';
  };

  const specialEventTypes = ['CLINIC', 'KIDS NIGHT OUT', 'OPEN GYM'];
  const summerCampTypes = ['SUMMER CAMP - FULL DAY', 'SUMMER CAMP - GYMNASTICS', 'SUMMER CAMP - NINJA'];

  // Event filtering and processing
  const allEventTypes = useMemo(() => {
    const types = [...new Set(eventsData.map(event => event.type))];
    return types.sort();
  }, [eventsData]);

  const filteredAndSortedEvents = useMemo(() => {
    let filtered = eventsData.filter(event => {
      const matchesGym = selectedGym === 'all' || event.gym_name === selectedGym;
      const matchesType = selectedEventType === 'all' || event.type === selectedEventType;
      const matchesSearch = searchTerm === '' || 
        (event.title && event.title.toLowerCase().includes(searchTerm.toLowerCase())) ||
        (event.date && event.date.toLowerCase().includes(searchTerm.toLowerCase())) ||
        (event.day_of_week && event.day_of_week.toLowerCase().includes(searchTerm.toLowerCase()));
      
      return matchesGym && matchesType && matchesSearch;
    });

    // Sort events
    filtered.sort((a, b) => {
      let aValue, bValue;
      
      switch (sortBy) {
        case 'gym':
          aValue = a.gym_name || '';
          bValue = b.gym_name || '';
          break;
        case 'title':
          aValue = a.title || '';
          bValue = b.title || '';
          break;
        case 'type':
          aValue = a.type || '';
          bValue = b.type || '';
          break;
        case 'price':
          aValue = a.price || '';
          bValue = b.price || '';
          break;
        case 'date':
        default:
          aValue = new Date(a.date || '1970-01-01');
          bValue = new Date(b.date || '1970-01-01');
          break;
      }

      if (sortBy === 'date') {
        return sortOrder === 'asc' ? aValue - bValue : bValue - aValue;
      } else {
        const comparison = aValue.toString().localeCompare(bValue.toString());
        return sortOrder === 'asc' ? comparison : -comparison;
      }
    });

    return filtered;
  }, [eventsData, selectedGym, selectedEventType, searchTerm, sortBy, sortOrder]);

  // Statistics calculations
  const specialEvents = useMemo(() => {
    return eventsData.filter(event => specialEventTypes.includes(event.type));
  }, [eventsData]);

  const summerCampEvents = useMemo(() => {
    return eventsData.filter(event => summerCampTypes.includes(event.type));
  }, [eventsData]);

  const missingSpecialEvents = useMemo(() => {
    const missing = [];
    
    allGyms.forEach(gym => {
      specialEventTypes.forEach(eventType => {
        const hasEvent = specialEvents.some(event => 
          event.gym_name === gym && event.type === eventType
        );
        
        if (!hasEvent) {
          missing.push({ gym, eventType });
        }
      });
    });
    
    return missing;
  }, [allGyms, specialEvents]);

  // Calendar helper functions
  const getDaysInMonth = (year, month) => {
    return new Date(year, month + 1, 0).getDate();
  };

  const getFirstDayOfMonth = (year, month) => {
    return new Date(year, month, 1).getDay();
  };

  const formatDateForDisplay = (dateString) => {
    if (!dateString) return 'No date';
    const date = new Date(dateString);
    return date.toLocaleDateString('en-US', { 
      month: 'short', 
      day: 'numeric'
    });
  };

  const formatDateForInput = (dateString) => {
    if (!dateString) return '';
    const date = new Date(dateString);
    return date.toISOString().split('T')[0];
  };

  const getDayOfWeek = (dateString) => {
    if (!dateString) return '';
    const date = new Date(dateString);
    return date.toLocaleDateString('en-US', { weekday: 'long' });
  };

  const isToday = (dateString) => {
    if (!dateString) return false;
    const date = new Date(dateString);
    const today = new Date();
    return date.toDateString() === today.toDateString();
  };

  // Event handlers
  const handleSort = (newSortBy) => {
    if (sortBy === newSortBy) {
      setSortOrder(sortOrder === 'asc' ? 'desc' : 'asc');
    } else {
      setSortBy(newSortBy);
      setSortOrder('asc');
    }
  };

  const handleSelectEvent = (eventId) => {
    const newSelected = new Set(selectedEvents);
    if (newSelected.has(eventId)) {
      newSelected.delete(eventId);
    } else {
      newSelected.add(eventId);
    }
    setSelectedEvents(newSelected);
  };

  const handleSelectAll = () => {
    if (selectedEvents.size === filteredAndSortedEvents.length) {
      setSelectedEvents(new Set());
    } else {
      setSelectedEvents(new Set(filteredAndSortedEvents.map(event => event.id)));
    }
  };

  const copyToClipboard = async (text, eventId) => {
    try {
      await navigator.clipboard.writeText(text);
      setCopiedStates(prev => ({ ...prev, [eventId]: true }));
      setTimeout(() => {
        setCopiedStates(prev => ({ ...prev, [eventId]: false }));
      }, 2000);
    } catch (err) {
      console.error('Failed to copy text:', err);
    }
  };

  const exportEvents = (format) => {
    const selectedEventData = filteredAndSortedEvents.filter(event => 
      selectedEvents.has(event.id)
    );
    
    if (selectedEventData.length === 0) {
      alert('Please select events to export');
      return;
    }

    let content;
    let filename;
    let mimeType;

    switch (format) {
      case 'csv':
        const headers = ['Gym', 'Title', 'Date', 'Time', 'Price', 'Type', 'Day of Week', 'URL'];
        const csvRows = [
          headers.join(','),
          ...selectedEventData.map(event => [
            `"${event.gym_name || ''}"`,
            `"${event.title || ''}"`,
            `"${event.date || ''}"`,
            `"${event.time || ''}"`,
            `"${event.price || ''}"`,
            `"${event.type || ''}"`,
            `"${event.day_of_week || ''}"`,
            `"${event.event_url || ''}"`
          ].join(','))
        ];
        content = csvRows.join('\n');
        filename = 'events.csv';
        mimeType = 'text/csv';
        break;

      case 'json':
        content = JSON.stringify(selectedEventData, null, 2);
        filename = 'events.json';
        mimeType = 'application/json';
        break;

      case 'summary':
        const summary = selectedEventData.map(event => 
          `${event.gym_name} - ${event.title} (${formatDateForDisplay(event.date)})`
        ).join('\n');
        content = summary;
        filename = 'events-summary.txt';
        mimeType = 'text/plain';
        break;

      default:
        return;
    }

    const blob = new Blob([content], { type: mimeType });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = filename;
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);
  };

  const scrollToTop = () => {
    topRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  const scrollToGym = (gymName) => {
    if (viewMode === 'calendar') {
      setHighlightedGym(gymName);
      setTimeout(() => setHighlightedGym(null), 3000);
    }
    
    gymRefs.current[gymName]?.scrollIntoView({ 
      behavior: 'smooth',
      block: 'start'
    });
  };

  const jumpToCalendarView = () => {
    setViewMode('calendar');
    setTimeout(() => {
      calendarSectionRef.current?.scrollIntoView({ 
        behavior: 'smooth',
        block: 'start'
      });
    }, 100);
  };

  // Calendar navigation
  const navigateCalendar = (direction) => {
    if (direction === 'prev') {
      if (calendarView === 'secondHalf') {
        setCalendarView('firstHalf');
        setCurrentDateRange({ start: 1, end: 15 });
      } else {
        // Could implement month navigation here
      }
    } else {
      if (calendarView === 'firstHalf') {
        setCalendarView('secondHalf');
        setCurrentDateRange({ start: 16, end: 30 });
      } else {
        // Could implement month navigation here
      }
    }
  };

  // Event form handlers
  const handleAddEvent = () => {
    setNewEvent({
      gym_id: "",
      title: "",
      date: "",
      time: "",
      price: "",
      day_of_week: "",
      type: "",
      event_url: ""
    });
    setEditingEvent(null);
    setShowAddForm(true);
  };

  const startEditEvent = (event) => {
    setNewEvent({
      gym_id: event.gym_id,
      title: event.title,
      date: formatDateForInput(event.date),
      time: event.time,
      price: event.price,
      day_of_week: event.day_of_week,
      type: event.type,
      event_url: event.event_url
    });
    setEditingEvent(event);
    setShowAddForm(true);
  };

  const handleDateChange = (date) => {
    const dayOfWeek = getDayOfWeek(date);
    setNewEvent(prev => ({
      ...prev,
      date,
      day_of_week: dayOfWeek
    }));
  };

  const handleSaveEvent = async () => {
    if (!newEvent.gym_id || !newEvent.title || !newEvent.date || !newEvent.type) {
      alert('Please fill in all required fields (Gym, Title, Date, Type)');
      return;
    }

    setIsSubmitting(true);
    
    try {
      if (editingEvent) {
        const { error } = await updateEvent(editingEvent.id, newEvent);
        if (error) {
          alert('Error updating event: ' + error);
          return;
        }
      } else {
        const { error } = await createEvent(newEvent);
        if (error) {
          alert('Error creating event: ' + error);
          return;
        }
      }
      
      setShowAddForm(false);
      setEditingEvent(null);
      setNewEvent({
        gym_id: "",
        title: "",
        date: "",
        time: "",
        price: "",
        day_of_week: "",
        type: "",
        event_url: ""
      });
    } catch (error) {
      alert('Error saving event: ' + error.message);
    } finally {
      setIsSubmitting(false);
    }
  };

  const handleDeleteEvent = async (eventId) => {
    if (!window.confirm('Are you sure you want to delete this event?')) {
      return;
    }

    try {
      const { error } = await deleteEvent(eventId);
      if (error) {
        alert('Error deleting event: ' + error);
      }
    } catch (error) {
      alert('Error deleting event: ' + error.message);
    }
  };

  const handleBulkDelete = async () => {
    if (selectedEvents.size === 0) {
      alert('Please select events to delete');
      return;
    }

    if (!window.confirm(`Are you sure you want to delete ${selectedEvents.size} selected events?`)) {
      return;
    }

    try {
      for (const eventId of selectedEvents) {
        await deleteEvent(eventId);
      }
      setSelectedEvents(new Set());
    } catch (error) {
      alert('Error deleting events: ' + error.message);
    }
  };

  // Scroll handling
  useEffect(() => {
    const handleScroll = () => {
      const scrolled = window.scrollY > 300;
      setShowBackToTop(scrolled);

      if (calendarSectionRef.current) {
        const calendarRect = calendarSectionRef.current.getBoundingClientRect();
        const isInView = calendarRect.top < window.innerHeight && calendarRect.bottom > 0;
        setIsInCalendarView(isInView);
        setShowJumpToGym(viewMode === 'calendar' && isInView);
      }
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, [viewMode]);

  // Loading and error states
  if (gymsLoading || eventsLoading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="text-center">
          <Loader className="w-8 h-8 animate-spin mx-auto mb-4" style={{ color: '#b48f8f' }} />
          <p style={{ color: '#4a4a4a' }}>Loading events...</p>
        </div>
      </div>
    );
  }

  if (gymsError || eventsError) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="text-center text-red-600">
          <AlertCircle className="w-8 h-8 mx-auto mb-4" />
          <p>Error loading data: {gymsError || eventsError}</p>
        </div>
      </div>
    );
  }

  // Render component
  return (
    <div className="min-h-screen" style={{ backgroundColor: '#f8f7f6' }}>
      <div ref={topRef} className="max-w-7xl mx-auto p-6">
        {/* Header */}
        <div className="mb-8">
          <h1 className="text-4xl font-bold mb-2" style={{ color: '#4a4a4a' }}>
            June 2025 Gymnastics Events Dashboard
          </h1>
          <p className="text-lg" style={{ color: '#6b6b6b' }}>
            Managing {eventsData.length} events across {allGyms.length} gymnastics facilities
          </p>
        </div>

        {/* Statistics Cards */}
        <div className="grid grid-cols-1 md:grid-cols-4 gap-6 mb-8">
          <div className="bg-white rounded-lg shadow-md p-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm font-medium" style={{ color: '#6b6b6b' }}>Special Events</p>
                <p className="text-3xl font-bold" style={{ color: '#4a4a4a' }}>{specialEvents.length}</p>
                <p className="text-xs" style={{ color: '#6b6b6b' }}>CLINIC, KNO, Open Gym</p>
              </div>
              <Calendar className="w-8 h-8" style={{ color: '#b48f8f' }} />
            </div>
          </div>

          <div className="bg-white rounded-lg shadow-md p-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm font-medium" style={{ color: '#6b6b6b' }}>Summer Camps</p>
                <p className="text-3xl font-bold" style={{ color: '#4a4a4a' }}>{summerCampEvents.length}</p>
                <p className="text-xs" style={{ color: '#6b6b6b' }}>Full Day, Gymnastics, Ninja</p>
              </div>
              <Sun className="w-8 h-8" style={{ color: '#b48f8f' }} />
            </div>
          </div>

          <div className="bg-white rounded-lg shadow-md p-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm font-medium" style={{ color: '#6b6b6b' }}>Missing Events</p>
                <p className="text-3xl font-bold" style={{ color: '#4a4a4a' }}>{missingSpecialEvents.length}</p>
                <p className="text-xs" style={{ color: '#6b6b6b' }}>Gyms needing events</p>
              </div>
              <AlertCircle className="w-8 h-8" style={{ color: '#b48f8f' }} />
            </div>
          </div>

          <div className="bg-white rounded-lg shadow-md p-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm font-medium" style={{ color: '#6b6b6b' }}>Selected</p>
                <p className="text-3xl font-bold" style={{ color: '#4a4a4a' }}>{selectedEvents.size}</p>
                <p className="text-xs" style={{ color: '#6b6b6b' }}>Events for export</p>
              </div>
              <CheckCircle className="w-8 h-8" style={{ color: '#b48f8f' }} />
            </div>
          </div>
        </div>

        {/* Controls */}
        <div className="bg-white rounded-lg shadow-md p-6 mb-8">
          <div className="flex flex-wrap gap-4 items-center justify-between">
            {/* Filters */}
            <div className="flex flex-wrap gap-4 items-center">
              <div className="flex items-center gap-2">
                <Filter className="w-4 h-4" style={{ color: '#b48f8f' }} />
                <select
                  value={selectedGym}
                  onChange={(e) => setSelectedGym(e.target.value)}
                  className="border rounded-md px-3 py-2 text-sm"
                  style={{ borderColor: '#cec4c1' }}
                >
                  <option value="all">All Gyms</option>
                  {allGyms.map(gym => (
                    <option key={gym} value={gym}>{gym}</option>
                  ))}
                </select>
              </div>

              <select
                value={selectedEventType}
                onChange={(e) => setSelectedEventType(e.target.value)}
                className="border rounded-md px-3 py-2 text-sm"
                style={{ borderColor: '#cec4c1' }}
              >
                <option value="all">All Event Types</option>
                {allEventTypes.map(type => (
                  <option key={type} value={type}>{type}</option>
                ))}
              </select>

              <div className="flex items-center gap-2">
                <Search className="w-4 h-4" style={{ color: '#b48f8f' }} />
                <input
                  type="text"
                  placeholder="Search events..."
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                  className="border rounded-md px-3 py-2 text-sm w-64"
                  style={{ borderColor: '#cec4c1' }}
                />
              </div>
            </div>

            {/* View Controls */}
            <div className="flex items-center gap-2">
              <div className="flex bg-gray-100 rounded-md">
                <button
                  onClick={() => setViewMode('calendar')}
                  className={`px-3 py-2 text-sm rounded-l-md ${viewMode === 'calendar' ? 'text-white' : 'text-gray-600'}`}
                  style={{ backgroundColor: viewMode === 'calendar' ? '#b48f8f' : 'transparent' }}
                >
                  <Calendar className="w-4 h-4" />
                </button>
                <button
                  onClick={() => setViewMode('table')}
                  className={`px-3 py-2 text-sm ${viewMode === 'table' ? 'text-white' : 'text-gray-600'}`}
                  style={{ backgroundColor: viewMode === 'table' ? '#b48f8f' : 'transparent' }}
                >
                  <List className="w-4 h-4" />
                </button>
                <button
                  onClick={() => setViewMode('cards')}
                  className={`px-3 py-2 text-sm rounded-r-md ${viewMode === 'cards' ? 'text-white' : 'text-gray-600'}`}
                  style={{ backgroundColor: viewMode === 'cards' ? '#b48f8f' : 'transparent' }}
                >
                  <Grid className="w-4 h-4" />
                </button>
              </div>

              <button
                onClick={() => setShowAdminPanel(!showAdminPanel)}
                className={`px-4 py-2 text-sm rounded-md transition-colors ${showAdminPanel ? 'text-white' : 'text-gray-600 hover:bg-gray-100'}`}
                style={{ backgroundColor: showAdminPanel ? '#b48f8f' : 'transparent' }}
              >
                Admin Panel
              </button>

              <button
                onClick={handleAddEvent}
                className="flex items-center gap-2 px-4 py-2 text-white text-sm rounded-md hover:opacity-90 transition-opacity"
                style={{ backgroundColor: '#b48f8f' }}
              >
                <Plus className="w-4 h-4" />
                Add Event
              </button>
            </div>
          </div>
        </div>

        {/* Admin Panel */}
        {showAdminPanel && (
          <div className="bg-white rounded-lg shadow-md p-6 mb-8">
            <h2 className="text-xl font-bold mb-4" style={{ color: '#4a4a4a' }}>Admin Panel</h2>
            
            {/* Admin Stats */}
            <div className="grid grid-cols-1 md:grid-cols-4 gap-4 mb-6">
              <div className="bg-gray-50 rounded-lg p-4">
                <p className="text-sm font-medium" style={{ color: '#6b6b6b' }}>Total Events</p>
                <p className="text-2xl font-bold" style={{ color: '#4a4a4a' }}>{eventsData.length}</p>
              </div>
              <div className="bg-gray-50 rounded-lg p-4">
                <p className="text-sm font-medium" style={{ color: '#6b6b6b' }}>This Month</p>
                <p className="text-2xl font-bold" style={{ color: '#4a4a4a' }}>{eventsData.length}</p>
              </div>
              <div className="bg-yellow-50 rounded-lg p-4">
                <p className="text-sm font-medium text-yellow-700">Needs Attention</p>
                <p className="text-2xl font-bold text-yellow-800">
                  {eventsData.filter(e => !e.time || !e.price || !e.event_url).length}
                </p>
              </div>
              <div className="bg-blue-50 rounded-lg p-4">
                <p className="text-sm font-medium text-blue-700">Selected</p>
                <p className="text-2xl font-bold text-blue-800">{selectedEvents.size}</p>
              </div>
            </div>

            {/* Bulk Actions */}
            <div className="flex flex-wrap gap-4 items-center mb-6">
              <button
                onClick={handleSelectAll}
                className="flex items-center gap-2 px-4 py-2 text-sm border rounded-md hover:bg-gray-50"
                style={{ borderColor: '#cec4c1', color: '#4a4a4a' }}
              >
                <CheckSquare className="w-4 h-4" />
                {selectedEvents.size === filteredAndSortedEvents.length ? 'Deselect All' : 'Select All'}
              </button>

              <button
                onClick={handleBulkDelete}
                disabled={selectedEvents.size === 0}
                className="flex items-center gap-2 px-4 py-2 text-sm text-white rounded-md hover:opacity-90 disabled:opacity-50 disabled:cursor-not-allowed"
                style={{ backgroundColor: '#dc2626' }}
              >
                <Trash2 className="w-4 h-4" />
                Delete Selected ({selectedEvents.size})
              </button>

              <div className="flex items-center gap-2">
                <Download className="w-4 h-4" style={{ color: '#b48f8f' }} />
                <button
                  onClick={() => exportEvents('csv')}
                  disabled={selectedEvents.size === 0}
                  className="px-3 py-2 text-sm border rounded-md hover:bg-gray-50 disabled:opacity-50"
                  style={{ borderColor: '#cec4c1', color: '#4a4a4a' }}
                >
                  CSV
                </button>
                <button
                  onClick={() => exportEvents('json')}
                  disabled={selectedEvents.size === 0}
                  className="px-3 py-2 text-sm border rounded-md hover:bg-gray-50 disabled:opacity-50"
                  style={{ borderColor: '#cec4c1', color: '#4a4a4a' }}
                >
                  JSON
                </button>
                <button
                  onClick={() => exportEvents('summary')}
                  disabled={selectedEvents.size === 0}
                  className="px-3 py-2 text-sm border rounded-md hover:bg-gray-50 disabled:opacity-50"
                  style={{ borderColor: '#cec4c1', color: '#4a4a4a' }}
                >
                  Summary
                </button>
              </div>
            </div>

            {/* Events Needing Attention */}
            {eventsData.filter(e => !e.time || !e.price || !e.event_url).length > 0 && (
              <div className="mb-6">
                <h3 className="text-lg font-semibold mb-3" style={{ color: '#4a4a4a' }}>Events Needing Attention</h3>
                <div className="space-y-2">
                  {eventsData.filter(e => !e.time || !e.price || !e.event_url).slice(0, 5).map(event => (
                    <div key={event.id} className="flex items-center justify-between p-3 bg-yellow-50 border border-yellow-200 rounded-md">
                      <div className="flex-1">
                        <p className="font-medium" style={{ color: '#4a4a4a' }}>{event.title}</p>
                        <p className="text-sm" style={{ color: '#6b6b6b' }}>{event.gym_name}  {formatDateForDisplay(event.date)}</p>
                        <p className="text-xs text-yellow-700">
                          Missing: {[
                            !event.time && 'Time',
                            !event.price && 'Price', 
                            !event.event_url && 'URL'
                          ].filter(Boolean).join(', ')}
                        </p>
                      </div>
                      <button
                        onClick={() => startEditEvent(event)}
                        className="flex items-center gap-1 px-3 py-1 text-xs text-white rounded-md hover:opacity-90"
                        style={{ backgroundColor: '#b48f8f' }}
                      >
                        <Edit className="w-3 h-3" />
                        Fix
                      </button>
                    </div>
                  ))}
                </div>
              </div>
            )}

            {/* Quick Edit Table */}
            <div className="overflow-x-auto">
              <table className="w-full text-sm">
                <thead>
                  <tr className="border-b" style={{ borderColor: '#cec4c1' }}>
                    <th className="text-left p-2">
                      <CheckSquare className="w-4 h-4" />
                    </th>
                    <th className="text-left p-2" style={{ color: '#4a4a4a' }}>Status</th>
                    <th className="text-left p-2" style={{ color: '#4a4a4a' }}>Gym</th>
                    <th className="text-left p-2" style={{ color: '#4a4a4a' }}>Event</th>
                    <th className="text-left p-2" style={{ color: '#4a4a4a' }}>Date</th>
                    <th className="text-left p-2" style={{ color: '#4a4a4a' }}>Type</th>
                    <th className="text-left p-2" style={{ color: '#4a4a4a' }}>Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {filteredAndSortedEvents.slice(0, 10).map(event => {
                    const isComplete = event.time && event.price && event.event_url;
                    return (
                      <tr key={event.id} className="border-b hover:bg-gray-50" style={{ borderColor: '#f0f0f0' }}>
                        <td className="p-2">
                          <button onClick={() => handleSelectEvent(event.id)}>
                            {selectedEvents.has(event.id) ? 
                              <CheckSquare className="w-4 h-4" style={{ color: '#b48f8f' }} /> : 
                              <Square className="w-4 h-4 text-gray-400" />
                            }
                          </button>
                        </td>
                        <td className="p-2">
                          <div className={`w-3 h-3 rounded-full ${isComplete ? 'bg-green-500' : 'bg-yellow-500'}`} title={isComplete ? 'Complete' : 'Needs attention'}></div>
                        </td>
                        <td className="p-2" style={{ color: '#4a4a4a' }}>{event.gym_name}</td>
                        <td className="p-2" style={{ color: '#4a4a4a' }}>{event.title}</td>
                        <td className="p-2" style={{ color: '#4a4a4a' }}>{formatDateForDisplay(event.date)}</td>
                        <td className="p-2">
                          <span className={`px-2 py-1 rounded-full text-xs ${getEventTypeColor(event.type)}`}>
                            {event.type}
                          </span>
                        </td>
                        <td className="p-2">
                          <div className="flex gap-1">
                            <button
                              onClick={() => startEditEvent(event)}
                              className="p-1 hover:bg-gray-200 rounded"
                              title="Edit event"
                            >
                              <Edit className="w-4 h-4" />
                            </button>
                            <button
                              onClick={() => handleDeleteEvent(event.id)}
                              className="p-1 hover:bg-gray-200 rounded text-red-600"
                              title="Delete event"
                            >
                              <Trash2 className="w-4 h-4" />
                            </button>
                          </div>
                        </td>
                      </tr>
                    );
                  })}
                </tbody>
              </table>
            </div>
          </div>
        )}

        {/* Add/Edit Event Form */}
        {showAddForm && (
          <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
            <div className="bg-white rounded-lg shadow-xl p-6 w-full max-w-md mx-4">
              <h2 className="text-xl font-bold mb-4" style={{ color: '#4a4a4a' }}>
                {editingEvent ? 'Edit Event' : 'Add New Event'}
              </h2>
              
              <div className="space-y-4">
                <div>
                  <label className="block text-sm font-medium mb-1" style={{ color: '#4a4a4a' }}>
                    Gym *
                  </label>
                  <select
                    value={newEvent.gym_id}
                    onChange={(e) => setNewEvent(prev => ({ ...prev, gym_id: e.target.value }))}
                    className={`w-full border rounded-md px-3 py-2 text-sm ${newEvent.gym_id ? 'border-green-300 bg-green-50' : 'border-gray-300'}`}
                    required
                  >
                    <option value="">Select Gym</option>
                    {gyms.map(gym => (
                      <option key={gym.id} value={gym.id}>{gym.name}</option>
                    ))}
                  </select>
                </div>

                <div>
                  <label className="block text-sm font-medium mb-1" style={{ color: '#4a4a4a' }}>
                    Event Title *
                  </label>
                  <input
                    type="text"
                    value={newEvent.title}
                    onChange={(e) => setNewEvent(prev => ({ ...prev, title: e.target.value }))}
                    className={`w-full border rounded-md px-3 py-2 text-sm ${newEvent.title ? 'border-green-300 bg-green-50' : 'border-gray-300'}`}
                    placeholder="Enter event title"
                    required
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium mb-1" style={{ color: '#4a4a4a' }}>
                    Date *
                  </label>
                  <input
                    type="date"
                    value={newEvent.date}
                    onChange={(e) => handleDateChange(e.target.value)}
                    className={`w-full border rounded-md px-3 py-2 text-sm ${newEvent.date ? 'border-green-300 bg-green-50' : 'border-gray-300'}`}
                    required
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium mb-1" style={{ color: '#4a4a4a' }}>
                    Time
                  </label>
                  <input
                    type="text"
                    value={newEvent.time}
                    onChange={(e) => setNewEvent(prev => ({ ...prev, time: e.target.value }))}
                    className={`w-full border rounded-md px-3 py-2 text-sm ${newEvent.time ? 'border-green-300 bg-green-50' : 'border-yellow-300 bg-yellow-50'}`}
                    placeholder="e.g., 10:00 AM - 12:00 PM"
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium mb-1" style={{ color: '#4a4a4a' }}>
                    Price
                  </label>
                  <input
                    type="text"
                    value={newEvent.price}
                    onChange={(e) => setNewEvent(prev => ({ ...prev, price: e.target.value }))}
                    className={`w-full border rounded-md px-3 py-2 text-sm ${newEvent.price ? 'border-green-300 bg-green-50' : 'border-yellow-300 bg-yellow-50'}`}
                    placeholder="e.g., $25"
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium mb-1" style={{ color: '#4a4a4a' }}>
                    Event Type *
                  </label>
                  <select
                    value={newEvent.type}
                    onChange={(e) => setNewEvent(prev => ({ ...prev, type: e.target.value }))}
                    className={`w-full border rounded-md px-3 py-2 text-sm ${newEvent.type ? 'border-green-300 bg-green-50' : 'border-gray-300'}`}
                    required
                  >
                    <option value="">Select Type</option>
                    <option value="CLINIC">CLINIC</option>
                    <option value="KIDS NIGHT OUT">KIDS NIGHT OUT</option>
                    <option value="OPEN GYM">OPEN GYM</option>
                    <option value="SUMMER CAMP - FULL DAY">SUMMER CAMP - FULL DAY</option>
                    <option value="SUMMER CAMP - GYMNASTICS">SUMMER CAMP - GYMNASTICS</option>
                    <option value="SUMMER CAMP - NINJA">SUMMER CAMP - NINJA</option>
                  </select>
                </div>

                <div>
                  <label className="block text-sm font-medium mb-1" style={{ color: '#4a4a4a' }}>
                    Registration URL
                  </label>
                  <input
                    type="url"
                    value={newEvent.event_url}
                    onChange={(e) => setNewEvent(prev => ({ ...prev, event_url: e.target.value }))}
                    className={`w-full border rounded-md px-3 py-2 text-sm ${newEvent.event_url ? 'border-green-300 bg-green-50' : 'border-yellow-300 bg-yellow-50'}`}
                    placeholder="https://portal.iclasspro.com/..."
                  />
                </div>

                {newEvent.day_of_week && (
                  <div>
                    <label className="block text-sm font-medium mb-1" style={{ color: '#4a4a4a' }}>
                      Day of Week
                    </label>
                    <input
                      type="text"
                      value={newEvent.day_of_week}
                      readOnly
                      className="w-full border border-gray-300 rounded-md px-3 py-2 text-sm bg-gray-50"
                    />
                  </div>
                )}
              </div>

              <div className="mt-6 bg-gray-50 rounded-md p-3">
                <p className="text-xs text-gray-600 mb-2">Form Status:</p>
                <div className="flex gap-2 text-xs">
                  <span className={`px-2 py-1 rounded ${newEvent.gym_id && newEvent.title && newEvent.date && newEvent.type ? 'bg-green-100 text-green-700' : 'bg-red-100 text-red-700'}`}>
                    Required: {[newEvent.gym_id, newEvent.title, newEvent.date, newEvent.type].filter(Boolean).length}/4
                  </span>
                  <span className={`px-2 py-1 rounded ${newEvent.time && newEvent.price && newEvent.event_url ? 'bg-green-100 text-green-700' : 'bg-yellow-100 text-yellow-700'}`}>
                    Recommended: {[newEvent.time, newEvent.price, newEvent.event_url].filter(Boolean).length}/3
                  </span>
                </div>
              </div>

              <div className="flex gap-3 mt-6">
                <button
                  onClick={() => setShowAddForm(false)}
                  className="flex-1 px-4 py-2 text-sm border rounded-md hover:bg-gray-50"
                  style={{ borderColor: '#cec4c1', color: '#4a4a4a' }}
                >
                  Cancel
                </button>
                <button
                  onClick={handleSaveEvent}
                  disabled={isSubmitting || !newEvent.gym_id || !newEvent.title || !newEvent.date || !newEvent.type}
                  className="flex-1 flex items-center justify-center gap-2 px-4 py-2 text-sm text-white rounded-md hover:opacity-90 disabled:opacity-50 disabled:cursor-not-allowed"
                  style={{ backgroundColor: '#b48f8f' }}
                >
                  {isSubmitting ? (
                    <>
                      <Loader className="w-4 h-4 animate-spin" />
                      Saving...
                    </>
                  ) : (
                    <>
                      <Save className="w-4 h-4" />
                      {editingEvent ? 'Update Event' : 'Create Event'}
                    </>
                  )}
                </button>
              </div>
            </div>
          </div>
        )}

        {/* Missing Events Alert */}
        {missingSpecialEvents.length > 0 && (
          <div className="bg-yellow-50 border-l-4 border-yellow-400 p-4 mb-8">
            <div className="flex">
              <AlertCircle className="w-5 h-5 text-yellow-400 mr-3 mt-0.5" />
              <div>
                <h3 className="text-sm font-medium text-yellow-800">
                  Missing Special Events ({missingSpecialEvents.length})
                </h3>
                <div className="mt-2 text-sm text-yellow-700">
                  <ul className="space-y-1">
                    {missingSpecialEvents.slice(0, 5).map((missing, index) => (
                      <li key={index}>
                        <strong>{missing.gym}</strong> is missing: {missing.eventType}
                      </li>
                    ))}
                    {missingSpecialEvents.length > 5 && (
                      <li className="text-xs">...and {missingSpecialEvents.length - 5} more</li>
                    )}
                  </ul>
                </div>
              </div>
            </div>
          </div>
        )}

        {/* Jump to Gym Navigation */}
        {showJumpToGym && viewMode === 'calendar' && (
          <div className="fixed right-6 top-1/2 transform -translate-y-1/2 bg-white rounded-lg shadow-lg p-4 z-30 max-h-96 overflow-y-auto">
            <h3 className="text-sm font-medium mb-3" style={{ color: '#4a4a4a' }}>Jump to Gym</h3>
            <div className="space-y-2">
              {allGyms.map(gym => (
                <button
                  key={gym}
                  onClick={() => scrollToGym(gym)}
                  className={`block w-full text-left px-3 py-2 text-xs rounded hover:bg-gray-100 transition-colors ${highlightedGym === gym ? 'bg-yellow-100' : ''}`}
                  style={{ color: '#4a4a4a' }}
                >
                  {gym}
                </button>
              ))}
            </div>
          </div>
        )}

        {/* Calendar Navigation */}
        {viewMode === 'calendar' && (
          <div ref={calendarSectionRef} className="mb-8">
            <div className="bg-white rounded-lg shadow-md p-6">
              <div className="flex items-center justify-between mb-6">
                <h2 className="text-2xl font-bold" style={{ color: '#4a4a4a' }}>
                  June 2025 Calendar - {calendarView === 'firstHalf' ? 'First Half (1-15)' : 'Second Half (16-30)'}
                </h2>
                <div className="flex items-center gap-2">
                  <button
                    onClick={() => navigateCalendar('prev')}
                    disabled={calendarView === 'firstHalf'}
                    className="p-2 rounded-md hover:bg-gray-100 disabled:opacity-50 disabled:cursor-not-allowed"
                    title="Previous period"
                  >
                    <ChevronLeft className="w-5 h-5" />
                  </button>
                  <button
                    onClick={() => navigateCalendar('next')}
                    disabled={calendarView === 'secondHalf'}
                    className="p-2 rounded-md hover:bg-gray-100 disabled:opacity-50 disabled:cursor-not-allowed"
                    title="Next period"
                  >
                    <ChevronRight className="w-5 h-5" />
                  </button>
                </div>
              </div>

              {/* Calendar Grid */}
              <div className="grid grid-cols-7 gap-2">
                {/* Day headers */}
                {['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'].map(day => (
                  <div key={day} className="text-center font-medium py-2" style={{ color: '#6b6b6b' }}>
                    {day}
                  </div>
                ))}

                {/* Calendar days */}
                {(() => {
                  const year = 2025;
                  const month = 5; // June (0-indexed)
                  const daysInMonth = getDaysInMonth(year, month);
                  const firstDay = getFirstDayOfMonth(year, month);
                  const days = [];

                  // Add empty cells for days before the first day of the month
                  for (let i = 0; i < firstDay; i++) {
                    days.push(<div key={`empty-${i}`} className="h-24"></div>);
                  }

                  // Add the days of the month
                  for (let day = currentDateRange.start; day <= Math.min(currentDateRange.end, daysInMonth); day++) {
                    const dateStr = `2025-06-${day.toString().padStart(2, '0')}`;
                    const dayEvents = filteredAndSortedEvents.filter(event => event.date === dateStr);
                    const isCurrentDay = isToday(dateStr);

                    days.push(
                      <div
                        key={day}
                        className={`h-24 border rounded-md p-1 overflow-hidden ${isCurrentDay ? 'bg-blue-50 border-blue-200' : 'bg-white border-gray-200'} hover:shadow-md transition-shadow`}
                      >
                        <div className={`text-sm font-medium mb-1 ${isCurrentDay ? 'text-blue-600' : 'text-gray-900'}`}>
                          {day}
                        </div>
                        <div className="space-y-1">
                          {dayEvents.slice(0, 2).map(event => (
                            <div
                              key={event.id}
                              className={`text-xs p-1 rounded cursor-pointer hover:opacity-80 ${getEventTypeColor(event.type)}`}
                              onClick={() => startEditEvent(event)}
                              title={`${event.gym_name} - ${event.title}\n${event.time || 'No time'}\n${event.price || 'No price'}`}
                            >
                              <div className="truncate font-medium">{event.title}</div>
                              <div className="truncate opacity-75">{event.gym_name}</div>
                            </div>
                          ))}
                          {dayEvents.length > 2 && (
                            <div className="text-xs text-gray-500 pl-1">
                              +{dayEvents.length - 2} more
                            </div>
                          )}
                        </div>
                      </div>
                    );
                  }

                  return days;
                })()}
              </div>
            </div>
          </div>
        )}

        {/* Table View */}
        {viewMode === 'table' && (
          <div className="bg-white rounded-lg shadow-md overflow-hidden mb-8">
            <div className="p-6 border-b" style={{ borderColor: '#cec4c1' }}>
              <div className="flex items-center justify-between">
                <h2 className="text-xl font-bold" style={{ color: '#4a4a4a' }}>Events Table</h2>
                <button
                  onClick={handleSelectAll}
                  className="flex items-center gap-2 px-3 py-1 text-sm border rounded-md hover:bg-gray-50"
                  style={{ borderColor: '#cec4c1', color: '#4a4a4a' }}
                >
                  <CheckSquare className="w-4 h-4" />
                  {selectedEvents.size === filteredAndSortedEvents.length ? 'Deselect All' : 'Select All'}
                </button>
              </div>
            </div>
            
            <div className="overflow-x-auto">
              <table className="w-full">
                <thead className="bg-gray-50">
                  <tr>
                    <th className="text-left p-4">
                      <CheckSquare className="w-4 h-4" />
                    </th>
                    <th className="text-left p-4" style={{ color: '#4a4a4a' }}>
                      <button
                        onClick={() => handleSort('gym')}
                        className="flex items-center gap-1 hover:text-gray-600"
                      >
                        Gym
                        {sortBy === 'gym' && (
                          sortOrder === 'asc' ? <SortAsc className="w-4 h-4" /> : <SortDesc className="w-4 h-4" />
                        )}
                      </button>
                    </th>
                    <th className="text-left p-4" style={{ color: '#4a4a4a' }}>
                      <button
                        onClick={() => handleSort('title')}
                        className="flex items-center gap-1 hover:text-gray-600"
                      >
                        Event
                        {sortBy === 'title' && (
                          sortOrder === 'asc' ? <SortAsc className="w-4 h-4" /> : <SortDesc className="w-4 h-4" />
                        )}
                      </button>
                    </th>
                    <th className="text-left p-4" style={{ color: '#4a4a4a' }}>
                      <button
                        onClick={() => handleSort('date')}
                        className="flex items-center gap-1 hover:text-gray-600"
                      >
                        Date
                        {sortBy === 'date' && (
                          sortOrder === 'asc' ? <SortAsc className="w-4 h-4" /> : <SortDesc className="w-4 h-4" />
                        )}
                      </button>
                    </th>
                    <th className="text-left p-4" style={{ color: '#4a4a4a' }}>Time</th>
                    <th className="text-left p-4" style={{ color: '#4a4a4a' }}>
                      <button
                        onClick={() => handleSort('price')}
                        className="flex items-center gap-1 hover:text-gray-600"
                      >
                        Price
                        {sortBy === 'price' && (
                          sortOrder === 'asc' ? <SortAsc className="w-4 h-4" /> : <SortDesc className="w-4 h-4" />
                        )}
                      </button>
                    </th>
                    <th className="text-left p-4" style={{ color: '#4a4a4a' }}>
                      <button
                        onClick={() => handleSort('type')}
                        className="flex items-center gap-1 hover:text-gray-600"
                      >
                        Type
                        {sortBy === 'type' && (
                          sortOrder === 'asc' ? <SortAsc className="w-4 h-4" /> : <SortDesc className="w-4 h-4" />
                        )}
                      </button>
                    </th>
                    <th className="text-left p-4" style={{ color: '#4a4a4a' }}>Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {(() => {
                    const groupedEvents = {};
                    filteredAndSortedEvents.forEach(event => {
                      if (!groupedEvents[event.gym_name]) {
                        groupedEvents[event.gym_name] = [];
                      }
                      groupedEvents[event.gym_name].push(event);
                    });

                    return Object.entries(groupedEvents).map(([gymName, events]) => (
                      <React.Fragment key={gymName}>
                        <tr className="bg-gray-50">
                          <td colSpan="8" className="p-4">
                            <div
                              ref={el => gymRefs.current[gymName] = el}
                              className="font-semibold text-lg"
                              style={{ color: '#4a4a4a' }}
                            >
                              {gymName} ({events.length} events)
                            </div>
                          </td>
                        </tr>
                        {events.map((event, eventIndex) => (
                          <tr key={event.id} className={`border-b hover:bg-gray-50 ${selectedEvents.has(event.id) ? 'bg-rose-50' : ''}`} style={{ borderColor: '#f0f0f0' }}>
                            <td className="p-4">
                              <button onClick={() => handleSelectEvent(event.id)}>
                                {selectedEvents.has(event.id) ? 
                                  <CheckSquare className="w-5 h-5" style={{ color: '#b48f8f' }} /> : 
                                  <Square className="w-5 h-5 text-gray-400" />
                                }
                              </button>
                            </td>
                            <td className="p-4" style={{ color: '#4a4a4a' }}>{event.gym_name}</td>
                            <td className="p-4" style={{ color: '#4a4a4a' }}>{event.title}</td>
                            <td className="p-4" style={{ color: '#4a4a4a' }}>
                              {formatDateForDisplay(event.date)}
                              {event.day_of_week && (
                                <div className="text-xs text-gray-500">({event.day_of_week})</div>
                              )}
                            </td>
                            <td className="p-4" style={{ color: '#4a4a4a' }}>{event.time || '-'}</td>
                            <td className="p-4" style={{ color: '#4a4a4a' }}>{event.price || '-'}</td>
                            <td className="p-4">
                              <span className={`px-2 py-1 rounded-full text-xs font-medium ${getEventTypeColor(event.type)}`}>
                                {event.type}
                              </span>
                            </td>
                            <td className="p-4">
                              <div className="flex gap-2">
                                {event.event_url && (
                                  <a
                                    href={event.event_url}
                                    target="_blank"
                                    rel="noopener noreferrer"
                                    className="p-2 hover:bg-gray-200 rounded"
                                    title="Open registration page"
                                  >
                                    <ExternalLink className="w-4 h-4" />
                                  </a>
                                )}
                                {event.event_url && (
                                  <button
                                    onClick={() => copyToClipboard(event.event_url, event.id)}
                                    className="p-2 hover:bg-gray-200 rounded"
                                    title="Copy URL"
                                  >
                                    {copiedStates[event.id] ? 
                                      <CheckCircle className="w-4 h-4 text-green-600" /> : 
                                      <Copy className="w-4 h-4" />
                                    }
                                  </button>
                                )}
                                <button
                                  onClick={() => startEditEvent(event)}
                                  className="p-2 hover:bg-gray-200 rounded"
                                  title="Edit event"
                                >
                                  <Edit className="w-4 h-4" />
                                </button>
                                <button
                                  onClick={() => handleDeleteEvent(event.id)}
                                  className="p-2 hover:bg-gray-200 rounded text-red-600"
                                  title="Delete event"
                                >
                                  <Trash2 className="w-4 h-4" />
                                </button>
                              </div>
                            </td>
                          </tr>
                        ))}
                      </React.Fragment>
                    ));
                  })()}
                </tbody>
              </table>
            </div>
          </div>
        )}

        {/* Cards View */}
        {viewMode === 'cards' && (
          <div className="space-y-6">
            {allGyms.filter(gym => {
              if (selectedGym !== 'all' && gym !== selectedGym) return false;
              const matchingEvents = eventsData.filter(event => {
                const matchesGym = event.gym_name === gym;
                const matchesType = selectedEventType === 'all' || event.type === selectedEventType;
                const matchesSearch = searchTerm === '' || 
                  (event.title && event.title.toLowerCase().includes(searchTerm.toLowerCase())) ||
                  (event.date && event.date.toLowerCase().includes(searchTerm.toLowerCase())) ||
                  (event.day_of_week && event.day_of_week.toLowerCase().includes(searchTerm.toLowerCase()));
                return matchesType && matchesSearch;
              });
              return matchingEvents.length > 0;
            }).map((gym, gymIndex) => {
              const gymEvents = eventsData.filter(event => event.gym_name === gym);
              const gymData = gyms.find(g => g.name === gym);
              
              return (
                <div 
                  key={gymIndex} 
                  className="bg-white rounded-lg shadow-md overflow-hidden"
                  ref={el => gymRefs.current[gym] = el}
                >
                  <div className="text-white p-6" style={{ background: 'linear-gradient(to right, #b48f8f, #b4acb0)' }}>
                    <h2 className="text-2xl font-bold mb-2">{gym}</h2>
                    <div className="flex flex-wrap gap-4 text-sm opacity-90">
                      <div className="flex items-center gap-1">
                        <MapPin className="w-4 h-4" />
                        {gymData?.address || "No address"}
                      </div>
                      <div className="flex items-center gap-1">
                        <Phone className="w-4 h-4" />
                        {gymData?.phone || "No phone"}
                      </div>
                    </div>
                  </div>

                  <div className="p-6">
                    <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-3">
                      {gymEvents
                        .filter(event => {
                          const matchesType = selectedEventType === 'all' || event.type === selectedEventType;
                          const matchesSearch = searchTerm === '' || 
                            (event.title && event.title.toLowerCase().includes(searchTerm.toLowerCase())) ||
                            (event.date && event.date.toLowerCase().includes(searchTerm.toLowerCase())) ||
                            (event.day_of_week && event.day_of_week.toLowerCase().includes(searchTerm.toLowerCase()));
                          return matchesType && matchesSearch;
                        })
                        .map((event, eventIndex) => (
                        <div key={eventIndex} className={`border-2 rounded-lg p-4 hover:shadow-md transition-all ${
                          selectedEvents.has(event.id) ? 'bg-rose-50' : 'border-gray-200'
                        }`} style={{ 
                          borderColor: selectedEvents.has(event.id) ? '#b48f8f' : '#cec4c1'
                        }}>
                          <div className="flex items-start justify-between mb-3">
                            <button
                              onClick={() => handleSelectEvent(event.id)}
                              className="mr-2 mt-1"
                            >
                              {selectedEvents.has(event.id) ? 
                                <CheckSquare className="w-5 h-5" style={{ color: '#b48f8f' }} /> : 
                                <Square className="w-5 h-5 text-gray-400" />
                              }
                            </button>
                            <h3 className="font-semibold text-sm flex-1" style={{ color: '#4a4a4a' }}>{event.title || 'Untitled Event'}</h3>
                            {event.type && (
                              <span className={`px-2 py-1 rounded-full text-xs font-medium border ${getEventTypeColor(event.type)}`}>
                                {event.type}
                              </span>
                            )}
                          </div>
                          
                          <div className="space-y-2 text-sm ml-7" style={{ color: '#4a4a4a' }}>
                            <div className="flex items-center gap-2">
                              <Calendar className="w-4 h-4" />
                              <span>{formatDateForDisplay(event.date)} {event.day_of_week ? `(${event.day_of_week})` : ''}</span>
                            </div>
                            <div className="flex items-center gap-2">
                              <Clock className="w-4 h-4" />
                              <span>{event.time || 'No time'}</span>
                            </div>
                            <div className="flex items-center gap-2">
                              <DollarSign className="w-4 h-4" />
                              <span>{event.price || 'No price'}</span>
                            </div>
                          </div>

                          <div className="mt-4 ml-7 space-y-2">
                            <div className="flex gap-2 flex-wrap">
                              {event.event_url && (
                                <a
                                  href={event.event_url}
                                  target="_blank"
                                  rel="noopener noreferrer"
                                  className="flex items-center gap-1 px-3 py-2 text-white text-xs rounded-md hover:opacity-90 transition-opacity"
                                  style={{ backgroundColor: '#b48f8f' }}
                                >
                                  <ExternalLink className="w-3 h-3" />
                                  Book Now
                                </a>
                              )}
                              <button
                                onClick={() => startEditEvent(event)}
                                className="flex items-center gap-1 px-3 py-2 text-xs rounded-md hover:bg-gray-200 transition-colors"
                                style={{ 
                                  backgroundColor: '#cec4c1', 
                                  color: '#4a4a4a' 
                                }}
                              >
                                <Edit className="w-3 h-3" />
                                Edit
                              </button>
                              {event.event_url && (
                                <button
                                  onClick={() => copyToClipboard(event.event_url, event.id)}
                                  className="flex items-center gap-1 px-3 py-2 text-xs rounded-md hover:bg-gray-200 transition-colors"
                                  style={{ 
                                    backgroundColor: '#cec4c1', 
                                    color: '#4a4a4a' 
                                  }}
                                >
                                  {copiedStates[event.id] ? 
                                    <CheckCircle className="w-3 h-3 text-green-600" /> : 
                                    <Copy className="w-3 h-3" />
                                  }
                                  Copy URL
                                </button>
                              )}
                            </div>
                          </div>
                        </div>
                      ))}
                    </div>
                  </div>
                </div>
              );
            })}
          </div>
        )}

        {/* Back to Top Button */}
        {showBackToTop && (
          <button
            onClick={scrollToTop}
            className="fixed bottom-6 right-6 text-white p-3 rounded-full shadow-lg transition-all duration-300 z-40 hover:opacity-90"
            style={{ backgroundColor: '#b48f8f' }}
            title="Back to top"
          >
            <ChevronUp className="w-5 h-5" />
          </button>
        )}

        {filteredAndSortedEvents.length === 0 && (
          <div className="text-center py-12 bg-white rounded-lg shadow-md">
            <p style={{ color: '#4a4a4a' }}>No events found matching your filters.</p>
          </div>
        )}

        <div className="mt-8 text-center text-sm" style={{ color: '#4a4a4a' }}>
          Total: {eventsData.length} events across {allGyms.length} gyms | 
          Showing: {filteredAndSortedEvents.length} events | 
          Selected: {selectedEvents.size} events
        </div>
      </div>
    </div>
  );
};

export default EnhancedJuneEventsDashboard;
