import React, { useState, useMemo, useRef, useEffect } from 'react';
import { 
  Calendar, Clock, DollarSign, MapPin, Filter, Search, Grid, List, Plus, 
  ChevronUp, ChevronLeft, ChevronRight, AlertCircle, Loader, Copy, CheckCircle
} from 'lucide-react';

// Import real API functions
import { gymsApi, eventsApi } from '../lib/api';

// Exact Color Theme from user's specification
const theme = {
  colors: {
    primary: '#b48f8f',        // Blush pink - primary brand highlight
    secondary: '#cec4c1',      // Neutral gray-beige - cards, secondary elements
    accent: '#8f93a0',         // Gray-blue - subtle highlights
    textPrimary: '#2c2c2c',    // Dark gray - main text
    textSecondary: '#6b6b6b',  // Medium gray - secondary text
    success: '#22c55e',        // Green - success states
    warning: '#f59e0b',        // Orange - warnings
    error: '#ef4444',          // Red - errors
    background: '#f8f9fa',     // Light background
  },
  gradients: {
    background: 'linear-gradient(135deg, #fdf7f7 0%, #f5f1f1 50%, #f0ebeb 100%)',
    card: 'linear-gradient(135deg, rgba(255,255,255,0.9) 0%, rgba(255,255,255,0.7) 100%)',
  }
};

// Custom hooks
const useGyms = () => {
  const [gyms, setGyms] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchGyms = async () => {
      try {
        const data = await gymsApi.getAll();
        setGyms(data || []);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchGyms();
  }, []);

  return { gyms, loading, error };
};

const useEvents = (startDate, endDate) => {
  const [events, setEvents] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchEvents = async () => {
      try {
        setLoading(true);
        const data = await eventsApi.getAll(startDate, endDate);
        setEvents(data || []);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    if (startDate && endDate) {
      fetchEvents();
    }
  }, [startDate, endDate]);

  return { events, loading, error, refetch: () => {
    // Re-fetch logic would go here
  }};
};

const EventsDashboard = () => {
  // State management
  const [selectedGym, setSelectedGym] = useState('all');
  const [selectedEventType, setSelectedEventType] = useState('all');
  const [searchTerm, setSearchTerm] = useState('');
  const [viewMode, setViewMode] = useState('calendar');
  const [currentMonth, setCurrentMonth] = useState(6); // July (0-indexed)
  const [currentYear, setCurrentYear] = useState(2025);
  const [calendarView, setCalendarView] = useState('firstHalf'); // Start with 1-15
  const [hoveredEvent, setHoveredEvent] = useState(null);
  const [showBackToTop, setShowBackToTop] = useState(false);
  const [copiedUrl, setCopiedUrl] = useState(null);
  
  // Refs
  const topRef = useRef(null);
  const calendarRef = useRef(null);
  const gymRefs = useRef({});

  // Fetch data
  const { gyms: gymsList, loading: gymsLoading, error: gymsError } = useGyms();
  
  const startDate = `${currentYear}-${String(currentMonth + 1).padStart(2, '0')}-01`;
  const endDate = new Date(currentYear, currentMonth + 1, 0).toISOString().split('T')[0];
  const { events, loading: eventsLoading, error: eventsError } = useEvents(startDate, endDate);

  // Helper functions
  const getDaysInMonth = (year, month) => {
    return new Date(year, month + 1, 0).getDate();
  };

  const daysInCurrentMonth = getDaysInMonth(currentYear, currentMonth);

  const displayDates = useMemo(() => {
    if (calendarView === 'firstHalf') {
      return Array.from({ length: 15 }, (_, i) => i + 1);
    } else {
      return Array.from({ length: daysInCurrentMonth - 15 }, (_, i) => i + 16);
    }
  }, [calendarView, daysInCurrentMonth]);

  const goToPreviousMonth = () => {
    if (currentMonth === 0) {
      setCurrentMonth(11);
      setCurrentYear(currentYear - 1);
    } else {
      setCurrentMonth(currentMonth - 1);
    }
    setCalendarView('firstHalf');
  };

  const goToNextMonth = () => {
    if (currentMonth === 11) {
      setCurrentMonth(0);
      setCurrentYear(currentYear + 1);
    } else {
      setCurrentMonth(currentMonth + 1);
    }
    setCalendarView('firstHalf');
  };

  const scrollToGym = (gymCode) => {
    const gymElement = gymRefs.current[gymCode];
    if (gymElement) {
      gymElement.scrollIntoView({ 
        behavior: 'smooth', 
        block: 'center' 
      });
      // Add a visual highlight effect
      gymElement.style.backgroundColor = theme.colors.secondary;
      setTimeout(() => {
        gymElement.style.backgroundColor = '';
      }, 2000);
    }
  };

  const scrollToTop = () => {
    topRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  // Scroll listener for back to top button
  useEffect(() => {
    const handleScroll = () => {
      setShowBackToTop(window.scrollY > 400);
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  // Data processing
  const eventTypes = useMemo(() => {
    const types = [...new Set(events.map(event => event.event_type))];
    return types.filter(Boolean).sort();
  }, [events]);

  const filteredEvents = useMemo(() => {
    return events.filter(event => {
      const matchesGym = selectedGym === 'all' || event.gym_code === selectedGym;
      const matchesType = selectedEventType === 'all' || event.event_type === selectedEventType;
      const matchesSearch = searchTerm === '' || 
        event.title?.toLowerCase().includes(searchTerm.toLowerCase()) ||
        event.event_type?.toLowerCase().includes(searchTerm.toLowerCase());
      
      return matchesGym && matchesType && matchesSearch;
    });
  }, [events, selectedGym, selectedEventType, searchTerm]);

  const uniqueGymsWithEvents = useMemo(() => {
    const gymCodes = [...new Set(filteredEvents.map(event => event.gym_code))];
    return gymCodes.sort();
  }, [filteredEvents]);

  const getEventTypeColor = (eventType) => {
    const colors = {
      'CLINIC': '#8b5cf6',
      'KIDS NIGHT OUT': '#06b6d4',
      'OPEN GYM': '#10b981',
      'SUMMER CAMP': '#f59e0b',
      'BIRTHDAY PARTY': '#ec4899',
      'SPECIAL EVENT': '#ef4444',
      'WORKSHOP': '#6366f1',
    };
    return colors[eventType] || theme.colors.accent;
  };

  const copyToClipboard = async (text, eventId) => {
    try {
      await navigator.clipboard.writeText(text);
      setCopiedUrl(eventId);
      setTimeout(() => setCopiedUrl(null), 2000);
    } catch (err) {
      console.error('Failed to copy:', err);
    }
  };

  // Loading states
  if (gymsLoading || eventsLoading) {
    return (
      <div className="flex items-center justify-center min-h-screen" style={{ backgroundColor: theme.colors.background }}>
        <div className="text-center">
          <Loader className="w-8 h-8 animate-spin mx-auto mb-4" style={{ color: theme.colors.primary }} />
          <p style={{ color: theme.colors.textSecondary }}>Loading events data...</p>
        </div>
      </div>
    );
  }

  if (gymsError || eventsError) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="text-center text-red-600">
          <AlertCircle className="w-8 h-8 mx-auto mb-4" />
          <p>Error loading data: {gymsError || eventsError}</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen" style={{ background: theme.gradients.background }}>
      <div ref={topRef} className="relative z-10 p-4">
        <div className="max-w-full mx-auto px-2">
          {/* Dashboard Header */}
          <div className="text-center mb-6">
            <h1 className="text-4xl font-bold bg-gradient-to-r from-pink-400 via-purple-400 to-indigo-400 bg-clip-text text-transparent mb-2">
              ✨ Master Events Hub ✨
            </h1>
            <p className="text-gray-600">Managing events across 10 gymnastics facilities</p>
          </div>

          {/* Controls */}
          <div className="mb-6 space-y-4">
            {/* Month Navigation */}
            <div className="flex justify-center items-center gap-4 mb-4">
              <button
                onClick={goToPreviousMonth}
                className="flex items-center gap-2 px-4 py-2 rounded-full text-white transition-all duration-200 hover:scale-105 hover:shadow-lg"
                style={{ backgroundColor: theme.colors.primary }}
              >
                <ChevronLeft className="w-4 h-4" />
                Previous
              </button>
              
              <h2 className="text-2xl font-bold px-6 py-2 rounded-full text-white shadow-md"
                  style={{ backgroundColor: theme.colors.accent }}>
                {new Date(currentYear, currentMonth).toLocaleDateString('en-US', { 
                  month: 'long', 
                  year: 'numeric' 
                })}
              </h2>
              
              <button
                onClick={goToNextMonth}
                className="flex items-center gap-2 px-4 py-2 rounded-full text-white transition-all duration-200 hover:scale-105 hover:shadow-lg"
                style={{ backgroundColor: theme.colors.primary }}
              >
                Next
                <ChevronRight className="w-4 h-4" />
              </button>
            </div>

            {/* Filters Row */}
            <div className="flex flex-wrap gap-4 justify-center items-center">
              <div className="relative">
                <Filter className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 w-4 h-4" />
                <select
                  value={selectedGym}
                  onChange={(e) => setSelectedGym(e.target.value)}
                  className="pl-10 pr-4 py-2 border rounded-lg focus:ring-2 focus:ring-pink-300 focus:border-pink-400 bg-white shadow-sm"
                >
                  <option value="all">All Gyms</option>
                  {gymsList.map(gym => (
                    <option key={gym.gym_code} value={gym.gym_code}>{gym.name}</option>
                  ))}
                </select>
              </div>

              <div className="relative">
                <Filter className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 w-4 h-4" />
                <select
                  value={selectedEventType}
                  onChange={(e) => setSelectedEventType(e.target.value)}
                  className="pl-10 pr-4 py-2 border rounded-lg focus:ring-2 focus:ring-pink-300 focus:border-pink-400 bg-white shadow-sm"
                >
                  <option value="all">All Event Types</option>
                  {eventTypes.map(type => (
                    <option key={type} value={type}>{type}</option>
                  ))}
                </select>
              </div>

              <div className="relative">
                <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 w-4 h-4" />
                <input
                  type="text"
                  placeholder="Search events..."
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                  className="pl-10 pr-4 py-2 border rounded-lg focus:ring-2 focus:ring-pink-300 focus:border-pink-400 bg-white shadow-sm"
                />
              </div>

              <button
                onClick={() => setViewMode(viewMode === 'calendar' ? 'table' : 'calendar')}
                className="flex items-center gap-2 px-4 py-2 rounded-lg bg-white border hover:bg-gray-50 transition-colors shadow-sm"
              >
                {viewMode === 'calendar' ? <List className="w-4 h-4" /> : <Grid className="w-4 h-4" />}
                {viewMode === 'calendar' ? 'Table View' : 'Calendar View'}
              </button>
            </div>
          </div>

          {/* Gym Quick Navigation */}
          {filteredEvents.length > 0 && uniqueGymsWithEvents.length > 3 && (
            <div className="mb-6">
              <h3 className="text-lg font-semibold mb-3 text-center text-gray-700">Quick Jump to Gym</h3>
              <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-5 xl:grid-cols-6 gap-3">
                {uniqueGymsWithEvents.map(gymCode => {
                  const gym = gymsList.find(g => g.gym_code === gymCode);
                  const eventCount = filteredEvents.filter(e => e.gym_code === gymCode).length;
                  
                  return (
                    <button
                      key={gymCode}
                      onClick={() => scrollToGym(gymCode)}
                      className="px-3 py-2 rounded-full text-white text-sm font-medium transition-all duration-150 hover:scale-105 hover:shadow-md flex items-center justify-center gap-2"
                      style={{ backgroundColor: theme.colors.primary }}
                    >
                      {gym?.name || gymCode}
                      <span className="bg-white bg-opacity-30 px-2 py-0.5 rounded-full text-xs">
                        {eventCount}
                      </span>
                    </button>
                  );
                })}
              </div>
            </div>
          )}

          {/* Calendar View */}
          {viewMode === 'calendar' && (
            <div className="space-y-6">
              {/* Calendar Navigation */}
              <div className="flex justify-center items-center gap-4 mb-4">
                <button
                  onClick={() => setCalendarView('firstHalf')}
                  className={`px-6 py-2 rounded-full font-medium transition-all duration-200 ${
                    calendarView === 'firstHalf'
                      ? 'text-white shadow-lg'
                      : 'text-gray-600 bg-white border hover:shadow-md'
                  }`}
                  style={calendarView === 'firstHalf' ? { backgroundColor: theme.colors.primary } : {}}
                >
                  Days 1-15
                </button>
                <button
                  onClick={() => setCalendarView('secondHalf')}
                  className={`px-6 py-2 rounded-full font-medium transition-all duration-200 ${
                    calendarView === 'secondHalf'
                      ? 'text-white shadow-lg'
                      : 'text-gray-600 bg-white border hover:shadow-md'
                  }`}
                  style={calendarView === 'secondHalf' ? { backgroundColor: theme.colors.primary } : {}}
                >
                  Days 16-{daysInCurrentMonth}
                </button>
              </div>

              {/* Calendar Grid - FULL WIDTH */}
              <div ref={calendarRef} className="w-full overflow-x-auto rounded-xl shadow-lg" style={{ backgroundColor: 'rgba(255, 255, 255, 0.95)' }}>
                <div className="min-w-full">
                  {/* Calendar Header */}
                  <div className="grid sticky top-0 z-20 border-b-2" 
                       style={{ 
                         gridTemplateColumns: `200px repeat(${displayDates.length}, 1fr)`,
                         backgroundColor: theme.colors.secondary,
                         borderColor: theme.colors.primary
                       }}>
                    {/* Gym Header */}
                    <div className="p-3 font-bold text-center border-r-2" style={{ borderColor: theme.colors.primary }}>
                      Gym
                    </div>
                    
                    {/* Date Headers */}
                    {displayDates.map(date => (
                      <div key={date} className="p-2 text-center font-medium border-r border-gray-200 min-w-0">
                        <div className="text-sm font-bold">{date}</div>
                        <div className="text-xs text-gray-600">
                          {new Date(currentYear, currentMonth, date).toLocaleDateString('en-US', { weekday: 'short' })}
                        </div>
                      </div>
                    ))}
                  </div>

                  {/* Calendar Body */}
                  <div className="divide-y divide-gray-200">
                    {uniqueGymsWithEvents.map(gymCode => {
                      const gym = gymsList.find(g => g.gym_code === gymCode);
                      const gymEvents = filteredEvents.filter(e => e.gym_code === gymCode);
                      
                      return (
                        <div
                          key={gymCode}
                          ref={el => gymRefs.current[gymCode] = el}
                          className="grid hover:bg-gray-50 transition-colors"
                          style={{ gridTemplateColumns: `200px repeat(${displayDates.length}, 1fr)` }}
                        >
                          {/* Gym Name Column */}
                          <div className="p-4 font-medium border-r-2 bg-gray-50 flex items-center justify-center"
                               style={{ borderColor: theme.colors.primary }}>
                            <div className="text-center">
                              <div className="font-bold text-sm">{gym?.name || gymCode}</div>
                              <div className="text-xs text-gray-600 mt-1">
                                {gymEvents.length} event{gymEvents.length !== 1 ? 's' : ''}
                              </div>
                            </div>
                          </div>
                          
                          {/* Date Columns */}
                          {displayDates.map(date => {
                            const dateEvents = gymEvents.filter(event => {
                              const eventDate = new Date(event.event_date);
                              return eventDate.getDate() === date;
                            });
                            
                            return (
                              <div key={`${gymCode}-${date}`} className="p-1 border-r border-gray-200 min-h-[80px] relative">
                                <div className="space-y-1">
                                  {dateEvents.map(event => (
                                    <div
                                      key={event.id}
                                      className="relative group cursor-pointer"
                                      onMouseEnter={(e) => setHoveredEvent({ event, position: { x: e.clientX, y: e.clientY } })}
                                      onMouseLeave={() => setHoveredEvent(null)}
                                    >
                                      <div
                                        className="text-xs p-1 rounded text-white text-center font-medium truncate hover:scale-105 transition-transform duration-150"
                                        style={{ backgroundColor: getEventTypeColor(event.event_type) }}
                                      >
                                        {event.event_type}
                                      </div>
                                    </div>
                                  ))}
                                </div>
                              </div>
                            );
                          })}
                        </div>
                      );
                    })}
                  </div>
                </div>
              </div>

              {/* Event Hover Popover */}
              {hoveredEvent && (
                <div
                  className="fixed z-50 pointer-events-none"
                  style={{
                    left: hoveredEvent.position.x + 10,
                    top: hoveredEvent.position.y - 100,
                  }}
                >
                  <div className="bg-white rounded-lg shadow-2xl p-4 w-80 border-2 relative backdrop-blur-sm" style={{
                    borderColor: theme.colors.primary,
                    background: theme.gradients.card
                  }}>
                    <div className="flex justify-between items-start mb-3">
                      <h4 className="font-bold text-lg" style={{ color: theme.colors.textPrimary }}>
                        {hoveredEvent.event.title || 'Event Details'}
                      </h4>
                    </div>
                    
                    <div className="space-y-2.5 text-sm" style={{ color: '#4a4a4a' }}>
                      <div className="flex items-center gap-2">
                        <MapPin className="w-4 h-4" style={{ color: theme.colors.accent }} />
                        <span>{gymsList.find(g => g.gym_code === hoveredEvent.event.gym_code)?.name || hoveredEvent.event.gym_code}</span>
                      </div>
                      <div className="flex items-center gap-2">
                        <Calendar className="w-4 h-4" style={{ color: theme.colors.accent }} />
                        <span>{new Date(hoveredEvent.event.event_date).toLocaleDateString()} - {new Date(hoveredEvent.event.event_date).toLocaleDateString('en-US', { weekday: 'long' })}</span>
                      </div>
                      <div className="flex items-center gap-2">
                        <Clock className="w-4 h-4" style={{ color: theme.colors.accent }} />
                        <span>{hoveredEvent.event.event_time || 'Time TBA'}</span>
                      </div>
                      <div className="flex items-center gap-2">
                        <DollarSign className="w-4 h-4" style={{ color: theme.colors.accent }} />
                        <span>{hoveredEvent.event.price || 'Price TBA'}</span>
                      </div>
                    </div>
                    
                    <div className="mt-3 pt-3 border-t flex gap-2" style={{ borderColor: '#cec4c1' }}>
                      {hoveredEvent.event.registration_url && (
                        <button
                          onClick={() => copyToClipboard(hoveredEvent.event.registration_url, hoveredEvent.event.id)}
                          className="flex items-center gap-2 px-3 py-1.5 rounded-md text-white text-xs font-medium hover:scale-105 transition-transform pointer-events-auto"
                          style={{ backgroundColor: copiedUrl === hoveredEvent.event.id ? theme.colors.success : theme.colors.primary }}
                        >
                          {copiedUrl === hoveredEvent.event.id ? <CheckCircle className="w-3 h-3" /> : <Copy className="w-3 h-3" />}
                          {copiedUrl === hoveredEvent.event.id ? 'Copied!' : 'Copy URL'}
                        </button>
                      )}
                    </div>
                    
                    {/* Triangle pointer */}
                    <div
                      className="absolute w-0 h-0"
                      style={{
                        left: '20px',
                        bottom: '-10px',
                        borderLeft: '10px solid transparent',
                        borderRight: '10px solid transparent',
                        borderTop: `10px solid ${theme.colors.primary}`
                      }}
                    />
                  </div>
                </div>
              )}
            </div>
          )}
        </div>
      </div>

      {/* Back to Top Button */}
      {showBackToTop && (
        <button
          onClick={scrollToTop}
          className="fixed bottom-6 right-6 w-12 h-12 rounded-full shadow-lg hover:scale-105 transition-transform duration-200 z-40"
          style={{ backgroundColor: theme.colors.primary }}
        >
          <ChevronUp className="w-6 h-6 text-white mx-auto" />
        </button>
      )}
    </div>
  );
};

export default EventsDashboard;
